      if (!existingUser) {
        console.log(`Admin API: User ID ${id} not found`);
        return res.status(404).json({ message: 'User not found' });
      }

      // Prevent admin from deleting themselves
      const currentUserId = req.user?.id;
      if (currentUserId === id) {
