  }

  .construction-hvac {
    background-color: var(--hvac-green);
  }

  /* Force table scrolling with visible scrollbars */
  .template-table-scroll {
    scrollbar-width: auto !important;
    scrollbar-color: #cbd5e1 #f1f5f9 !important;
