import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { DollarSign, FileText, Bot, Zap, Calculator, Info } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Drawing } from "@shared/schema";

interface ExtractionCostEstimatorProps {
  drawings: Drawing[];
  selectedDrawings?: Drawing[];
  onProceed?: () => void;
  onCancel?: () => void;
  extractionType: 'ai' | 'ocr' | 'both';
  estimatedPages?: number;
}

interface PaidFeature {
  id: string;
  name: string;
  costPerUnit: number;
  unit: string;
  enabled: boolean;
}

export default function ExtractionCostEstimator({
  drawings,
  selectedDrawings,
  onProceed,
  onCancel,
  extractionType,
  estimatedPages
}: ExtractionCostEstimatorProps) {
  const [totalCost, setTotalCost] = useState(0);
  const [breakdown, setBreakdown] = useState<Array<{name: string, pages: number, cost: number, unit: string}>>([]);

  // Fetch paid features pricing
  const { data: paidFeatures = [] } = useQuery<PaidFeature[]>({
    queryKey: ['/api/paid-features'],
  });

  useEffect(() => {
    calculateCost();
  }, [drawings, selectedDrawings, extractionType, paidFeatures, estimatedPages]);

  const calculateCost = () => {
    if (!paidFeatures.length) return;

    const targetDrawings = selectedDrawings || drawings;
    const totalPages = estimatedPages || targetDrawings.reduce((total, drawing) => {
      // Estimate pages based on file size (rough approximation: 100KB per page for PDFs)
      const estimatedPageCount = Math.max(1, Math.ceil(drawing.fileSize / 102400));
      return total + estimatedPageCount;
    }, 0);

    const costBreakdown: Array<{name: string, pages: number, cost: number, unit: string}> = [];
    let total = 0;

    // AI extraction cost
    if ((extractionType === 'ai' || extractionType === 'both')) {
      const aiFeature = paidFeatures.find(f => f.id === 'ai-extraction');
      if (aiFeature && aiFeature.enabled) {
        const aiCost = totalPages * aiFeature.costPerUnit;
        costBreakdown.push({
          name: 'AI-Enhanced Extraction',
          pages: totalPages,
          cost: aiCost,
          unit: aiFeature.unit
        });
        total += aiCost;
      }
    }

    // OCR processing cost
    if ((extractionType === 'ocr' || extractionType === 'both')) {
      const ocrFeature = paidFeatures.find(f => f.id === 'advanced-ocr');
      if (ocrFeature && ocrFeature.enabled) {
        const ocrCost = totalPages * ocrFeature.costPerUnit;
        costBreakdown.push({
          name: 'Advanced OCR Processing',
          pages: totalPages,
          cost: ocrCost,
          unit: ocrFeature.unit
        });
        total += ocrCost;
      }
    }

    // Premium PDF processing
    const pdfFeature = paidFeatures.find(f => f.id === 'premium-pdf-processing');
    if (pdfFeature && pdfFeature.enabled) {
      const pdfCost = totalPages * pdfFeature.costPerUnit;
      costBreakdown.push({
        name: 'Premium PDF Processing',
        pages: totalPages,
        cost: pdfCost,
        unit: pdfFeature.unit
      });
      total += pdfCost;
    }

    setBreakdown(costBreakdown);
    setTotalCost(total);
  };

  const formatCost = (cost: number) => {
    return cost < 0.01 ? '<$0.01' : `$${cost.toFixed(3)}`;
  };

  const targetDrawings = selectedDrawings || drawings;
  const totalPages = estimatedPages || targetDrawings.reduce((total, drawing) => {
    const estimatedPageCount = Math.max(1, Math.ceil(drawing.fileSize / 102400));
    return total + estimatedPageCount;
  }, 0);

  if (!targetDrawings.length || totalCost === 0) {
    return null;
  }

  return (
    <Card className="w-full max-w-md mx-auto border-blue-200 bg-blue-50 dark:bg-blue-900/20">
      <CardHeader className="pb-3">
        <CardTitle className="text-lg flex items-center space-x-2">
          <Calculator className="h-5 w-5 text-blue-600" />
          <span>Extraction Cost Estimate</span>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Summary */}
        <Alert className="border-blue-300 bg-blue-100 dark:bg-blue-800/30">
          <DollarSign className="h-4 w-4 text-blue-600" />
          <AlertDescription className="text-blue-800 dark:text-blue-200">
            <div className="grid grid-cols-2 gap-2 text-sm">
              <div>
                <span className="font-medium">Total Documents:</span>
                <p>{targetDrawings.length}</p>
              </div>
              <div>
                <span className="font-medium">Estimated Pages:</span>
                <p>{totalPages}</p>
              </div>
            </div>
          </AlertDescription>
        </Alert>

        {/* Cost Breakdown */}
        <div className="space-y-2">
          <h4 className="font-medium text-gray-900 dark:text-gray-100">Cost Breakdown:</h4>
          {breakdown.map((item, index) => (
            <div key={index} className="flex justify-between items-center py-2 px-3 bg-white dark:bg-gray-800 rounded-lg border">
              <div className="flex items-center space-x-2">
                {item.name.includes('AI') && <Bot className="h-4 w-4 text-blue-500" />}
                {item.name.includes('OCR') && <Zap className="h-4 w-4 text-green-500" />}
                {item.name.includes('PDF') && <FileText className="h-4 w-4 text-purple-500" />}
                <div>
                  <p className="text-sm font-medium">{item.name}</p>
                  <p className="text-xs text-gray-500">{item.pages} {item.unit}</p>
                </div>
              </div>
              <Badge variant="outline">{formatCost(item.cost)}</Badge>
            </div>
          ))}
        </div>

        {/* Total Cost */}
        <div className="flex justify-between items-center pt-3 border-t border-blue-200">
          <span className="text-lg font-semibold text-gray-900 dark:text-gray-100">Total Cost:</span>
          <Badge className="bg-blue-600 text-white text-lg px-3 py-1">
            {formatCost(totalCost)}
          </Badge>
        </div>

        {/* Action Buttons */}
        {onProceed && onCancel && (
          <div className="flex space-x-2 pt-2">
            <Button 
              variant="outline" 
              size="sm" 
              onClick={onCancel}
              className="flex-1"
            >
              Cancel
            </Button>
            <Button 
              size="sm" 
              onClick={onProceed}
              className="flex-1 bg-blue-600 hover:bg-blue-700"
            >
              Proceed ({formatCost(totalCost)})
            </Button>
          </div>
        )}

        {/* Pricing Information */}
        <Alert className="border-blue-200 bg-blue-50 dark:bg-blue-900/20">
          <Info className="h-4 w-4 text-blue-600" />
          <AlertDescription className="text-xs text-blue-800 dark:text-blue-200">
            <strong>AI Credit System:</strong> These costs reflect actual AI processing charges. Koncurent uses a centralized Anthropic API and charges per-token usage to your AI credit balance.
          </AlertDescription>
        </Alert>
      </CardContent>
    </Card>
  );
}

client/src/components/PaidFeaturesManage.content:
import React, { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Switch } from '@/components/ui/switch';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { DollarSign, Zap, Bot, Eye, Settings, Info, X, Key, Database, HardDrive, FileText, AlertTriangle } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { queryClient } from '@/lib/queryClient';
import APIKeyManager from './APIKeyManager';

interface PaidFeature {
  id: string;
  name: string;
  description: string;
  costType: 'external_api' | 'free' | 'replit_included';
  costPerUnit: number;
  unit: string;
  icon: React.ReactNode;
  enabled: boolean;
  category: 'paid_apis' | 'free_features' | 'replit_included';
  estimatedUsage?: string;
  apiKeyRequired?: string;
  hasApiKey?: boolean;
  provider?: string;
}

interface PaidFeaturesManagerProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function PaidFeaturesManager({ isOpen, onClose }: PaidFeaturesManagerProps) {
  const { toast } = useToast();

  // Fetch paid features configuration
  const { data: features = [], isLoading } = useQuery<PaidFeature[]>({
    queryKey: ['/api/paid-features'],
    enabled: isOpen,
  });

  // Fetch subscription status
  const { data: subscriptionStatus, isLoading: statusLoading } = useQuery({
    queryKey: ['/api/subscription/status'],
    enabled: isOpen,
  });

  // Toggle feature mutation
  const toggleFeatureMutation = useMutation({
    mutationFn: async ({ featureId, enabled }: { featureId: string; enabled: boolean }) => {
      const response = await fetch(`/api/paid-features/${featureId}/toggle`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ enabled }),
      });
      if (!response.ok) throw new Error('Failed to toggle feature');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/paid-features'] });
      queryClient.invalidateQueries({ queryKey: ['/api/ai-status'] });
      toast({ title: 'Feature setting updated successfully' });
    },
    onError: (error: any) => {
      const errorMessage = error?.message || 'Failed to update feature setting';
      toast({ 
        title: 'Feature Update Failed', 
        description: errorMessage.includes('Only paid features') ? 'Only paid features can be toggled. Free features are always enabled.' : errorMessage,
        variant: 'destructive' 
      });
    },
  });

  const handleToggleFeature = (featureId: string, enabled: boolean) => {
    toggleFeatureMutation.mutate({ featureId, enabled });
  };

  const getFeatureIcon = (feature: PaidFeature) => {
    if (feature.icon === 'Bot') return <Bot className="h-5 w-5 text-blue-500" />;
    if (feature.icon === 'Zap') return <Zap className="h-5 w-5 text-yellow-500" />;
    if (feature.icon === 'FileText') return <FileText className="h-5 w-5 text-purple-500" />;
    if (feature.icon === 'Database') return <Database className="h-5 w-5 text-green-500" />;
    if (feature.icon === 'HardDrive') return <HardDrive className="h-5 w-5 text-gray-500" />;
    return <Settings className="h-5 w-5 text-gray-500" />;
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'paid_apis': return 'bg-red-100 text-red-800';
      case 'free_features': return 'bg-green-100 text-green-800';
      case 'replit_included': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getCategoryName = (category: string) => {
    switch (category) {
      case 'paid_apis': return 'External APIs (Your Cost)';
      case 'free_features': return 'Free Open Source';
      case 'replit_included': return 'Included with Replit';
      default: return category;
    }
  };

  const getCostDisplay = (feature: PaidFeature) => {
    if (feature.costType === 'free') return 'Free';
    if (feature.costType === 'replit_included') return 'Included';
    if (feature.costType === 'external_api') return `~$${feature.costPerUnit.toFixed(3)} per ${feature.unit}`;
    return `$${feature.costPerUnit.toFixed(3)} per ${feature.unit}`;
  };

  const totalMonthlyCost = features
    .filter(f => f.enabled)
    .reduce((sum, f) => {
      const usage = parseFloat(f.estimatedUsage || '0');
      return sum + (f.costPerUnit * usage);
    }, 0);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white dark:bg-gray-900 rounded-lg max-w-4xl w-full mx-4 max-h-[90vh] overflow-hidden">
        <div className="p-6 border-b border-gray-200 dark:border-gray-700">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-2">
              <DollarSign className="h-5 w-5 text-green-600" />
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                Features & API Management
              </h2>
            </div>
            <Button variant="ghost" size="sm" onClick={onClose}>
              <X className="h-4 w-4" />
            </Button>
          </div>
          <p className="text-sm text-gray-600 dark:text-gray-300 mt-2">
            Configure features and manage API keys with transparent cost information
          </p>
        </div>

        <div className="p-6 overflow-y-auto max-h-[calc(90vh-180px)]">
          <Tabs defaultValue="subscription" className="w-full">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="subscription">Subscription</TabsTrigger>
              <TabsTrigger value="features">Features & Costs</TabsTrigger>
              <TabsTrigger value="api-keys">API Keys</TabsTrigger>
            </TabsList>

            <TabsContent value="subscription" className="space-y-6">
              {statusLoading ? (
                <div className="text-center py-8 text-gray-500">Loading subscription status...</div>
              ) : subscriptionStatus ? (
                <div className="space-y-4">
                  <Card>
                    <CardHeader>
                      <CardTitle className="flex items-center gap-2">
                        <DollarSign className="h-5 w-5" />
                        Current Subscription
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <div className="flex justify-between items-center">
                          <span className="text-sm font-medium">Plan:</span>
                          <Badge variant={subscriptionStatus.subscriptionStatus === 'active' ? 'default' : 'secondary'}>
                            {subscriptionStatus.subscriptionPlan === 'free' ? 'Free Trial' : subscriptionStatus.subscriptionPlan}
                          </Badge>
                        </div>
                        <div className="flex justify-between items-center">
                          <span className="text-sm font-medium">Status:</span>
                          <Badge variant={subscriptionStatus.subscriptionStatus === 'active' ? 'default' : 'secondary'}>
                            {subscriptionStatus.subscriptionStatus === 'active' ? 'Active' : 'Trial'}
                          </Badge>
                        </div>

                        {subscriptionStatus.subscriptionStatus === 'free_trial' && (
                          <div className="space-y-2">
                            <div className="flex justify-between items-center">
                              <span className="text-sm font-medium">Free Extractions Used:</span>
                              <span className="text-sm">{subscriptionStatus.trialExtractionsUsed}/100</span>
                            </div>
                            {subscriptionStatus.trialExtractionsUsed >= 100 && (
                              <Alert className="border-orange-200 bg-orange-50">
                                <AlertTriangle className="h-4 w-4 text-orange-600" />
                                <AlertDescription className="text-orange-800">
                                  You've used all 100 free extractions. Upgrade to continue using AI features.
                                </AlertDescription>
                              </Alert>
                            )}
                          </div>
                        )}

                        <div className="pt-3 border-t">
                          <h4 className="font-medium mb-2">Usage Limits</h4>
                          <div className="grid grid-cols-2 gap-4 text-sm">
                            <div>
                              <span className="text-gray-600">Drawings:</span>
                              <div className="font-medium">
                                {subscriptionStatus.limits.drawingCount} / {subscriptionStatus.limits.maxDrawings || '‚àû'}
                              </div>
                            </div>
                            <div>
                              <span className="text-gray-600">Extractions:</span>
                              <div className="font-medium">
                                {subscriptionStatus.limits.extractionCount} / {subscriptionStatus.limits.maxExtractions || '‚àû'}
                              </div>
                            </div>
                          </div>
                        </div>

                        {subscriptionStatus.subscriptionStatus !== 'active' && (
                          <div className="pt-3">
                            <Button 
                              className="w-full" 
                              onClick={() => window.location.href = '/pricing'}
                            >
                              Upgrade to Koncurent Pro
                            </Button>
                          </div>
                        )}
                      </div>
                    </CardContent>
                  </Card>

                  <Alert className="border-blue-200 bg-blue-50">
                    <Info className="h-4 w-4 text-blue-600" />
                    <AlertDescription className="text-blue-800">
                      <div className="space-y-2">
                        <div className="font-semibold">Koncurent Hi-LYTE Pricing Model</div>
                        <div className="text-sm">
                          ‚Ä¢ <strong>Platform Subscription:</strong> $29.99/month for unlimited uploads and features<br/>
                          ‚Ä¢ <strong>AI Processing:</strong> Requires your own Anthropic API key (pay-as-you-use)<br/>
                          ‚Ä¢ <strong>Free Trial:</strong> 1 free drawing upload and extraction to test the system
                        </div>
                      </div>
                    </AlertDescription>
                  </Alert>
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">Unable to load subscription status</div>
              )}
            </TabsContent>

            <TabsContent value="features" className="space-y-6">
              {/* Real Cost Explanation */}
              <Alert className="border-blue-200 bg-blue-50 dark:bg-blue-900/20">
                <Info className="h-4 w-4 text-blue-600" />
                <AlertDescription className="text-blue-800 dark:text-blue-200">
                  <div className="space-y-2">
                    <div className="font-semibold">‚úì Accurate Cost Information</div>
                    <div className="text-sm">
                      This shows the real costs for each feature. Only external APIs require your payment - 
                      everything else is free or included with your Replit plan.
                    </div>
                  </div>
                </AlertDescription>
              </Alert>

              {isLoading ? (
                <div className="text-center py-8 text-gray-500">Loading features...</div>
              ) : features.length === 0 ? (
                <div className="text-center py-8 text-gray-500">No features configured</div>
              ) : (
                <div className="space-y-4">
                  {['paid_apis', 'free_features', 'replit_included'].map(category => {
                    const categoryFeatures = features.filter(f => f.category === category);
                    if (categoryFeatures.length === 0) return null;

                    return (
                      <Card key={category} className={`border-l-4 ${
                        category === 'paid_apis' ? 'border-l-red-500' : 
                        category === 'free_features' ? 'border-l-green-500' : 
                        'border-l-blue-500'
                      }`}>
                        <CardHeader>
                          <div className="flex items-center justify-between">
                            <div className="flex items-center space-x-2">
                              {category === 'paid_apis' && <DollarSign className="h-5 w-5 text-red-500" />}
                              {category === 'free_features' && <Zap className="h-5 w-5 text-green-500" />}
                              {category === 'replit_included' && <Settings className="h-5 w-5 text-blue-500" />}
                              <CardTitle className="text-lg">
                                {getCategoryName(category)}
                              </CardTitle>
                            </div>
                            <Badge className={getCategoryColor(category)}>
                              {categoryFeatures.filter(f => f.enabled).length} active
                            </Badge>
                          </div>
                        </CardHeader>
                        <CardContent>
                          <div className="space-y-4">
                            {categoryFeatures.map((feature) => (
                              <div
                                key={feature.id}
                                className="flex items-center justify-between p-4 border border-gray-200 dark:border-gray-700 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                              >
                                <div className="flex-1">
                                  <div className="flex items-center space-x-3 mb-2">
                                    {getFeatureIcon(feature)}
                                    <h3 className="font-medium text-gray-900 dark:text-white">
                                      {feature.name}
                                    </h3>
                                    <Badge 
                                      variant={feature.enabled ? "default" : "secondary"}
                                      className="text-xs"
                                    >
                                      {feature.enabled ? 'Active' : 'Disabled'}
                                    </Badge>
                                    {feature.apiKeyRequired && !feature.hasApiKey && (
                                      <Badge variant="destructive" className="text-xs">
                                        API Key Required
                                      </Badge>
                                    )}
                                  </div>
                                  <p className="text-sm text-gray-600 dark:text-gray-300 mb-2">
                                    {feature.description}
                                  </p>
                                  <div className="flex items-center space-x-4 text-xs text-gray-500">
                                    <span className="flex items-center space-x-1">
                                      <DollarSign className="h-3 w-3" />
                                      <span className="font-medium">{getCostDisplay(feature)}</span>
                                    </span>
                                    {feature.estimatedUsage && feature.estimatedUsage !== 'unlimited' && feature.estimatedUsage !== 'included' && (
                                      <span className="flex items-center space-x-1">
                                        <Info className="h-3 w-3" />
                                        <span>~{feature.estimatedUsage} {feature.unit}/month</span>
                                      </span>
                                    )}
                                  </div>
                                </div>
                                <div className="flex items-center space-x-3">
                                  {feature.costType === 'external_api' ? (
                                    <Switch
                                      checked={feature.enabled}
                                      onCheckedChange={(checked) => handleToggleFeature(feature.id, checked)}
                                      disabled={toggleFeatureMutation.isPending || (feature.apiKeyRequired && !feature.hasApiKey)}
                                    />
                                  ) : (
                                    <Badge variant="outline" className="text-xs text-green-600 bg-green-50 border-green-200">
                                      Always On
                                    </Badge>
                                  )}
                                </div>
                              </div>
                            ))}
                          </div>
                        </CardContent>
                      </Card>
                    );
                  })}
                </div>
              )}
            </TabsContent>

            <TabsContent value="api-keys" className="space-y-6">
              <APIKeyManager onApiKeyUpdate={() => {
                queryClient.invalidateQueries({ queryKey: ['/api/paid-features'] });
                queryClient.invalidateQueries({ queryKey: ['/api/ai-status'] });
              }} />
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </div>
  );
}

client/src/components/PricingWizard.tsx.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { Progress } from '@/components/ui/progress';
import { 
  ArrowRight, 
  ArrowLeft, 
  Check, 
  Building, 
  Users, 
  FileText, 
  Zap,
  Crown,
  ChevronRight
} from 'lucide-react';

interface WizardQuestion {
  id: string;
  question: string;
  options: {
    value: string;
    label: string;
    description?: string;
  }[];
}

interface PlanRecommendation {
  planId: 'free' | 'pro' | 'enterprise';
  planName: string;
  confidence: number;
  reasons: string[];
  pricing: string;
  icon: any;
}

const wizardQuestions: WizardQuestion[] = [
  {
    id: 'company_size',
    question: 'What size is your organization?',
    options: [
      { value: 'individual', label: 'Individual/Freelancer', description: 'Just me working on projects' },
      { value: 'small_team', label: 'Small Team (2-10 people)', description: 'Small construction team or firm' },
      { value: 'medium_company', label: 'Medium Company (11-50 people)', description: 'Growing construction company' },
      { value: 'large_enterprise', label: 'Large Enterprise (50+ people)', description: 'Major construction firm or GC' }
    ]
  },
  {
    id: 'monthly_drawings',
    question: 'How many drawings do you process monthly?',
    options: [
      { value: 'few', label: '1-5 drawings', description: 'Occasional project documentation' },
      { value: 'moderate', label: '6-25 drawings', description: 'Regular project work' },
      { value: 'high', label: '26-100 drawings', description: 'High-volume processing' },
      { value: 'enterprise', label: '100+ drawings', description: 'Enterprise-level volume' }
    ]
  },
  {
    id: 'collaboration_needs',
    question: 'Do you need team collaboration features?',
    options: [
      { value: 'none', label: 'No collaboration needed', description: 'Working independently' },
      { value: 'basic', label: 'Basic sharing', description: 'Share results with a few people' },
      { value: 'team', label: 'Team collaboration', description: 'Multiple people working together' },
      { value: 'enterprise', label: 'Enterprise collaboration', description: 'Large team coordination' }
    ]
  },
  {
    id: 'budget_priority',
    question: 'What\'s your budget priority?',
    options: [
      { value: 'free', label: 'Free/Trial first', description: 'Want to test before committing' },
      { value: 'value', label: 'Best value for money', description: 'Good features at reasonable price' },
      { value: 'premium', label: 'Premium features', description: 'Need advanced capabilities' },
      { value: 'enterprise', label: 'Custom enterprise solution', description: 'Tailored to organization needs' }
    ]
  },
  {
    id: 'use_case',
    question: 'What\'s your primary use case?',
    options: [
      { value: 'testing', label: 'Testing the platform', description: 'Evaluating Hi-LYTE capabilities' },
      { value: 'project_work', label: 'Active project work', description: 'Regular construction document processing' },
      { value: 'business_ops', label: 'Core business operations', description: 'Essential to daily operations' },
      { value: 'procurement', label: 'Procurement management', description: 'Need procurement tracking features' }
    ]
  }
];

export default function PricingWizard({ onClose, onRecommendation }: {
  onClose: () => void;
  onRecommendation: (plan: PlanRecommendation) => void;
}) {
  const [currentStep, setCurrentStep] = useState(0);
  const [answers, setAnswers] = useState<Record<string, string>>({});
  const [showRecommendation, setShowRecommendation] = useState(false);

  const progress = ((currentStep + 1) / wizardQuestions.length) * 100;

  const handleAnswer = (questionId: string, value: string) => {
    setAnswers(prev => ({ ...prev, [questionId]: value }));
  };

  const [recommendation, setRecommendation] = useState<PlanRecommendation | null>(null);

  const nextStep = () => {
    if (currentStep < wizardQuestions.length - 1) {
      setCurrentStep(prev => prev + 1);
    } else {
      const result = generateRecommendation();
      setRecommendation(result);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(prev => prev - 1);
    }
  };

  const generateRecommendation = (): PlanRecommendation => {
    const {
      company_size,
      monthly_drawings,
      collaboration_needs,
      budget_priority,
      use_case
    } = answers;

    let recommendation: PlanRecommendation;

    // Scoring system for plan recommendation
    let freeScore = 0;
    let proScore = 0;
    let enterpriseScore = 0;

    // Company size scoring
    if (company_size === 'individual') {
      freeScore += 3; proScore += 1;
    } else if (company_size === 'small_team') {
      freeScore += 1; proScore += 3; enterpriseScore += 1;
    } else if (company_size === 'medium_company') {
      proScore += 2; enterpriseScore += 3;
    } else if (company_size === 'large_enterprise') {
      enterpriseScore += 4;
    }

    // Monthly drawings scoring
    if (monthly_drawings === 'few') {
      freeScore += 3; proScore += 1;
    } else if (monthly_drawings === 'moderate') {
      proScore += 3; enterpriseScore += 1;
    } else if (monthly_drawings === 'high') {
      proScore += 2; enterpriseScore += 3;
    } else if (monthly_drawings === 'enterprise') {
      enterpriseScore += 4;
    }

    // Collaboration needs scoring
    if (collaboration_needs === 'none') {
      freeScore += 2; proScore += 1;
    } else if (collaboration_needs === 'basic') {
      proScore += 3;
    } else if (collaboration_needs === 'team') {
      proScore += 2; enterpriseScore += 2;
    } else if (collaboration_needs === 'enterprise') {
      enterpriseScore += 4;
    }

    // Budget priority scoring
    if (budget_priority === 'free') {
      freeScore += 4;
    } else if (budget_priority === 'value') {
      proScore += 3;
    } else if (budget_priority === 'premium') {
      proScore += 1; enterpriseScore += 2;
    } else if (budget_priority === 'enterprise') {
      enterpriseScore += 4;
    }

    // Use case scoring
    if (use_case === 'testing') {
      freeScore += 4;
    } else if (use_case === 'project_work') {
      proScore += 3;
    } else if (use_case === 'business_ops') {
      proScore += 2; enterpriseScore += 2;
    } else if (use_case === 'procurement') {
      enterpriseScore += 4;
    }

    // Determine recommendation based on highest score
    const maxScore = Math.max(freeScore, proScore, enterpriseScore);
    let confidence = Math.min(95, Math.max(60, (maxScore / 20) * 100));

    if (maxScore === freeScore) {
      recommendation = {
        planId: 'free',
        planName: 'Free Trial',
        confidence,
        reasons: [
          'Perfect for testing Hi-LYTE capabilities',
          'No financial commitment required',
          'Great for individual use or small projects',
          'Includes basic OCR and PDF viewing'
        ],
        pricing: 'Free',
        icon: Zap
      };
    } else if (maxScore === proScore) {
      recommendation = {
        planId: 'pro',
        planName: 'Hi-LYTE Pro',
        confidence,
        reasons: [
          'Unlimited drawing uploads and extractions',
          'Advanced OCR processing capabilities',
          'Real-time collaboration features',
          'Best value for active construction work'
        ],
        pricing: '$19.99/month',
        icon: Crown
      };
    } else {
      recommendation = {
        planId: 'enterprise',
        planName: 'Koncurent Pro',
        confidence,
        reasons: [
          'Includes procurement management platform',
          'Supports large team collaboration',
          'Custom integrations and branding',
          'Dedicated support and account management'
        ],
        pricing: 'Contact Us /project /year',
        icon: Building
      };
    }

    setShowRecommendation(true);
    onRecommendation(recommendation);
    return recommendation;
  };

  const currentQuestion = wizardQuestions[currentStep];
  const canProceed = answers[currentQuestion?.id];

  if (showRecommendation && recommendation) {

    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
        <Card className="w-full max-w-2xl">
          <CardHeader className="text-center">
            <div className="mx-auto mb-4">
              <div className="w-16 h-16 rounded-full bg-green-100 dark:bg-green-900/20 flex items-center justify-center">
                <Check className="w-8 h-8 text-green-600" />
              </div>
            </div>
            <CardTitle className="text-2xl">Perfect Match Found!</CardTitle>
            <CardDescription>
              Based on your answers, we recommend the following plan
            </CardDescription>
          </CardHeader>

          <CardContent className="space-y-6">
            <Card className="border-2 border-blue-500 bg-blue-50/50 dark:bg-blue-900/20">
              <CardHeader className="text-center">
                <div className="flex items-center justify-center gap-2 mb-2">
                  <recommendation.icon className="w-6 h-6 text-blue-600" />
                  <CardTitle className="text-xl">{recommendation.planName}</CardTitle>
                </div>
                <Badge variant="secondary" className="bg-green-100 text-green-700">
                  {recommendation.confidence}% Match
                </Badge>
                <div className="text-2xl font-bold text-blue-600 mt-2">
                  {recommendation.pricing}
                </div>
              </CardHeader>

              <CardContent>
                <div className="space-y-3">
                  <h4 className="font-semibold">Why this plan is perfect for you:</h4>
                  {recommendation.reasons.map((reason, index) => (
                    <div key={index} className="flex items-start gap-2">
                      <Check className="w-4 h-4 text-green-600 mt-0.5 flex-shrink-0" />
                      <span className="text-sm">{reason}</span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            <div className="flex gap-3">
              <Button 
                onClick={() => {
                  onClose();
                  // Navigate to appropriate action based on plan
                  if (recommendation.planId === 'free') {
                    // Start free trial
                  } else if (recommendation.planId === 'pro') {
                    // Go to subscription page
                    window.location.href = '/subscription';
                  } else {
                    // Contact sales
                  }
                }}
                className="flex-1"
              >
                Get Started with {recommendation.planName}
                <ChevronRight className="w-4 h-4 ml-1" />
              </Button>
              <Button variant="outline" onClick={onClose}>
                Close
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-2xl">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Find Your Perfect Plan</CardTitle>
              <CardDescription>
                Answer a few questions to get a personalized recommendation
              </CardDescription>
            </div>
            <Button variant="ghost" size="sm" onClick={onClose}>
              √ó
            </Button>
          </div>

          <div className="space-y-2">
            <div className="flex justify-between text-sm text-gray-600">
              <span>Step {currentStep + 1} of {wizardQuestions.length}</span>
              <span>{Math.round(progress)}% complete</span>
            </div>
            <Progress value={progress} className="h-2" />
          </div>
        </CardHeader>

        <CardContent className="space-y-6">
          <div>
            <h3 className="text-lg font-medium mb-4">
              {currentQuestion.question}
            </h3>

            <RadioGroup
              value={answers[currentQuestion.id] || ''}
              onValueChange={(value) => handleAnswer(currentQuestion.id, value)}
              className="space-y-3"
            >
              {currentQuestion.options.map((option) => (
                <div key={option.value} className="flex items-start space-x-3">
                  <RadioGroupItem value={option.value} id={option.value} className="mt-1" />
                  <div className="flex-1">
                    <Label htmlFor={option.value} className="font-medium cursor-pointer">
                      {option.label}
                    </Label>
                    {option.description && (
                      <p className="text-sm text-gray-600 mt-1">{option.description}</p>
                    )}
                  </div>
                </div>
              ))}
            </RadioGroup>
          </div>

          <div className="flex justify-between pt-4">
            <Button
              variant="outline"
              onClick={prevStep}
              disabled={currentStep === 0}
            >
              <ArrowLeft className="w-4 h-4 mr-1" />
              Previous
            </Button>

            <Button
              onClick={nextStep}
              disabled={!canProceed}
            >
              {currentStep === wizardQuestions.length - 1 ? 'Get Recommendation' : 'Next'}
              <ArrowRight className="w-4 h-4 ml-1" />
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/TemplateManager.ts.content:
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Plus, Edit, Trash2, Save, Settings } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface TemplateColumn {
  name: string;
  type: 'text' | 'auto' | 'number' | 'date' | 'dimension' | 'boolean';
  description?: string;
  required?: boolean;
  example?: string;
}

interface ExtractionTemplate {
  id: string;
  name: string;
  description: string;
  columns: TemplateColumn[];
  divisionId: number;
}

interface Props {
  division: {
    id: number;
    name: string;
    code: string;
    color: string;
  };
  onTemplateUpdate?: () => void;
}

export function TemplateManager({ division, onTemplateUpdate }: Props) {
  const [template, setTemplate] = useState<ExtractionTemplate | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editingColumnIndex, setEditingColumnIndex] = useState<number | null>(null);
  const [newColumn, setNewColumn] = useState<TemplateColumn>({
    name: '',
    type: 'text',
    description: '',
    required: false,
    example: ''
  });
  const { toast } = useToast();

  // Load existing template
  useEffect(() => {
    loadTemplate();
  }, [division.id]);

  const loadTemplate = async () => {
    try {
      const response = await apiRequest(`/api/construction-divisions/${division.id}/template`, "GET");
      const data = await response.json();
      setTemplate(data);
    } catch (error) {
      console.error('Failed to load template:', error);
    }
  };

  const saveTemplate = async () => {
    if (!template) {
      console.error('No template to save');
      return;
    }

    console.log('Attempting to save template:', template);
    console.log('Division ID:', division.id);

    try {
      const response = await apiRequest(`/api/construction-divisions/${division.id}/template`, "POST", template);

      console.log('Response status:', response.status);
      console.log('Response headers:', response.headers);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Response error text:', errorText);
        throw new Error(`HTTP ${response.status}: ${errorText}`);
      }

      const result = await response.json();
      console.log('Template save response:', result);

      toast({
        title: "Template Saved",
        description: `Template for ${division.name} has been updated.`,
      });
      setIsEditing(false);
      onTemplateUpdate?.();
    } catch (error) {
      console.error('Template save error:', error);
      toast({
        title: "Error",
        description: `Failed to save template: ${error instanceof Error ? error.message : 'Unknown error'}`,
        variant: "destructive",
      });
    }
  };

  const createDefaultTemplate = () => {
    const defaultTemplate: ExtractionTemplate = {
      id: `custom-${division.id}-${Date.now()}`,
      name: `${division.name} Schedule`,
      description: `Custom extraction template for ${division.name}`,
      columns: [
        { name: 'Mark', type: 'text', description: 'Item mark/number', required: true },
        { name: 'Count', type: 'number', description: 'Quantity', required: true },
        { name: 'Description', type: 'text', description: 'Item description', required: true },
        { name: 'Comments', type: 'text', description: 'Additional notes' }
      ],
      divisionId: division.id
    };
    setTemplate(defaultTemplate);
    setIsEditing(true);
  };

  const addColumn = () => {
    console.log('Add column clicked!', { template: !!template, newColumnName: newColumn.name });
    if (!template || !newColumn.name.trim()) {
      console.log('Add column failed - missing template or name');
      return;
    }

    console.log('Adding column:', newColumn);
    setTemplate({
      ...template,
      columns: [...template.columns, { ...newColumn }]
    });

    setNewColumn({
      name: '',
      type: 'text',
      description: '',
      required: false,
      example: ''
    });

    console.log('Column added successfully');
  };

  const removeColumn = (index: number) => {
    if (!template) return;

    setTemplate({
      ...template,
      columns: template.columns.filter((_, i) => i !== index)
    });
  };

  const updateColumn = (index: number, updatedColumn: TemplateColumn) => {
    if (!template) return;

    const updatedColumns = [...template.columns];
    updatedColumns[index] = updatedColumn;
    setTemplate({
      ...template,
      columns: updatedColumns
    });
  };

  return (
    <Dialog>
      <DialogTrigger asChild>
        <button
          className="p-1.5 rounded hover:bg-gray-100 text-gray-500 hover:text-purple-600"
          title="Manage extraction template"
        >
          <Settings className="h-3 w-3" />
        </button>
      </DialogTrigger>

      <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>
            Extraction Template - {division.name}
          </DialogTitle>
        </DialogHeader>

        {!template ? (
          <div className="text-center py-8">
            <p className="text-muted-foreground mb-4">
              No extraction template configured for this division.
            </p>
            <Button onClick={createDefaultTemplate}>
              <Plus className="h-4 w-4 mr-2" />
              Create Template
            </Button>
          </div>
        ) : (
          <div className="space-y-6">
            {/* Template Info */}
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle>{template.name}</CardTitle>
                    <CardDescription>{template.description}</CardDescription>
                  </div>
                  <div className="flex gap-2">
                    {!isEditing ? (
                      <Button onClick={() => setIsEditing(true)} variant="outline" size="sm">
                        <Edit className="h-4 w-4 mr-2" />
                        Edit
                      </Button>
                    ) : (
                      <div className="flex gap-2">
                        <Button onClick={saveTemplate} size="sm">
                          <Save className="h-4 w-4 mr-2" />
                          Save
                        </Button>
                        <Button onClick={() => setIsEditing(false)} variant="outline" size="sm">
                          Cancel
                        </Button>
                      </div>
                    )}
                  </div>
                </div>
              </CardHeader>

              {isEditing && (
                <CardContent className="space-y-4">
                  <div>
                    <Label htmlFor="template-name">Template Name</Label>
                    <Input
                      id="template-name"
                      value={template.name}
                      onChange={(e) => setTemplate({ ...template, name: e.target.value })}
                    />
                  </div>
                  <div>
                    <Label htmlFor="template-description">Description</Label>
                    <Input
                      id="template-description"
                      value={template.description}
                      onChange={(e) => setTemplate({ ...template, description: e.target.value })}
                    />
                  </div>
                </CardContent>
              )}
            </Card>

            {/* Template Columns */}
            <Card>
              <CardHeader>
                <CardTitle>Template Columns</CardTitle>
                <CardDescription>
                  Define the structure that AI will use to extract data
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
