                <li>‚Ä¢ AI will analyze marquee selections and map data to these columns</li>
                <li>‚Ä¢ Missing values will be marked as null instead of being guessed</li>
                <li>‚Ä¢ Templates ensure consistent data structure across extractions</li>
                <li>‚Ä¢ You can customize templates per construction division</li>
              </ul>
            </div>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/TrainingNotificati.content:
import { useState, useEffect } from "react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Button } from "@/components/ui/button";
import { Brain, X, Lightbulb } from "lucide-react";

export function TrainingNotification() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Check if user has already seen the notification enough times
    const seenCount = parseInt(localStorage.getItem('trainingNotificationSeen') || '0');
    const maxShows = 2; // Show maximum 2 times

    if (seenCount < maxShows) {
      // Show notification after a short delay
      const timer = setTimeout(() => {
        setIsVisible(true);
        // Increment the seen count
        localStorage.setItem('trainingNotificationSeen', (seenCount + 1).toString());
      }, 2000);

      return () => clearTimeout(timer);
    }
  }, []);

  const handleDismiss = () => {
    setIsVisible(false);
    // Mark as seen when manually dismissed
    const currentCount = parseInt(localStorage.getItem('trainingNotificationSeen') || '0');
    localStorage.setItem('trainingNotificationSeen', Math.max(currentCount, 2).toString());
  };

  if (!isVisible) return null;

  return (
    <div className="fixed top-4 right-4 z-50 max-w-md">
      <Alert className="border-blue-200 bg-blue-50">
        <div className="flex items-start gap-3">
          <div className="flex items-center gap-2">
            <Brain className="h-4 w-4 text-blue-600" />
            <Lightbulb className="h-4 w-4 text-yellow-500" />
          </div>
          <div className="flex-1">
            <AlertDescription className="text-sm">
              <strong className="text-blue-800">AI Training Active!</strong>
              <br />
              Every manual highlight you make teaches the AI to better recognize data patterns. 
              Check the training dashboard to see your progress.
            </AlertDescription>
          </div>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleDismiss}
            className="h-6 w-6 p-0 text-gray-400 hover:text-gray-600"
          >
            <X className="h-3 w-3" />
          </Button>
        </div>
      </Alert>
    </div>
  );
}

client/src/components/add-column-form.ts.content:
import React, { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Plus, X } from "lucide-react";

interface TemplateColumn {
  name: string;
  type: 'text' | 'number' | 'date' | 'dimension' | 'boolean';
  description?: string;
  required?: boolean;
  example?: string;
}

interface AddColumnFormProps {
  onAddColumn: (column: { name: string; type: string; description?: string; required: boolean }) => void;
  onCancel: () => void;
  isLoading?: boolean;
}

export default function AddColumnForm({ onAddColumn, onCancel, isLoading }: AddColumnFormProps) {
  const [columnName, setColumnName] = useState('');
  const [columnType, setColumnType] = useState<'text' | 'number' | 'date' | 'dimension' | 'boolean'>('text');
  const [description, setDescription] = useState('');
  const [required, setRequired] = useState(false);
  const [example, setExample] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!columnName.trim()) return;

    const newColumn = {
      name: columnName.trim(),
      type: columnType,
      description: description.trim() || undefined,
      required
    };

    onAddColumn(newColumn);

    // Reset form
    setColumnName('');
    setColumnType('text');
    setDescription('');
    setRequired(false);
    setExample('');
  };

  return (
    <div className="border-t border-gray-200 p-4 bg-gray-50">
      <div className="flex items-center justify-between mb-3">
        <h4 className="text-sm font-medium text-gray-900">Add New Column</h4>
        <Button
          variant="ghost"
          size="sm"
          onClick={onCancel}
          className="h-6 w-6 p-0"
        >
          <X className="h-4 w-4" />
        </Button>
      </div>

      <form onSubmit={handleSubmit} className="space-y-3">
        <div className="grid grid-cols-2 gap-3">
          <div>
            <label className="text-xs font-medium text-gray-700 mb-1 block">
              Column Name <span className="text-red-500">*</span>
            </label>
            <Input
              value={columnName}
              onChange={(e) => setColumnName(e.target.value)}
              placeholder="e.g., Material, Finish"
              className="text-sm"
              required
            />
          </div>

          <div>
            <label className="text-xs font-medium text-gray-700 mb-1 block">
              Data Type
            </label>
            <Select value={columnType} onValueChange={(value: any) => setColumnType(value)}>
              <SelectTrigger className="text-sm">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="text">Text</SelectItem>
                <SelectItem value="number">Number</SelectItem>
                <SelectItem value="dimension">Dimension</SelectItem>
                <SelectItem value="date">Date</SelectItem>
                <SelectItem value="boolean">Yes/No</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        <div>
          <label className="text-xs font-medium text-gray-700 mb-1 block">
            Description
          </label>
          <Textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Describe what this column represents..."
            className="text-sm min-h-[60px]"
          />
        </div>

        <div>
          <label className="text-xs font-medium text-gray-700 mb-1 block">
            Example Value
          </label>
          <Input
            value={example}
            onChange={(e) => setExample(e.target.value)}
            placeholder="e.g., Steel, 6'-8&quot;, Yes"
            className="text-sm"
          />
        </div>

        <div className="flex items-center space-x-2">
          <input
            type="checkbox"
            id="required-checkbox"
            checked={required}
            onChange={(e) => setRequired(e.target.checked)}
            className="h-4 w-4 text-blue-600 rounded border-gray-300"
          />
          <label htmlFor="required-checkbox" className="text-xs text-gray-700">
            Required field
          </label>
        </div>

        <div className="flex justify-end space-x-2 pt-2">
          <Button
            type="button"
            variant="outline"
            size="sm"
            onClick={onCancel}
            disabled={isLoading}
          >
            Cancel
          </Button>
          <Button
            type="submit"
            size="sm"
            disabled={!columnName.trim() || isLoading}
          >
            <Plus className="h-4 w-4 mr-1" />
            {isLoading ? 'Adding...' : 'Add Column'}
          </Button>
        </div>
      </form>
    </div>
  );
}

