import React from 'react';
import { Brain, X } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface AIAutoAnalyzeProgressProps {
  isVisible: boolean;
  currentPage: number;
  totalPages: number;
  drawingName?: string;
  onCancel?: () => void;
}

export default function AIAutoAnalyzeProgress({
  isVisible,
  currentPage,
  totalPages,
  drawingName,
  onCancel
}: AIAutoAnalyzeProgressProps) {
  if (!isVisible) return null;

  const progress = totalPages > 0 ? (currentPage / totalPages) * 100 : 0;

  return (
    <div className="fixed bottom-4 right-4 z-50 bg-white rounded-lg shadow-lg border border-gray-200 p-4 w-80 animate-in slide-in-from-bottom-2">
      <div className="flex items-start space-x-3">
        <div className="flex-shrink-0">
          <div className="w-8 h-8 bg-purple-100 rounded-full flex items-center justify-center">
            <Brain className="w-4 h-4 text-purple-600" />
          </div>
        </div>

        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between mb-1">
            <h3 className="text-sm font-semibold text-gray-900">
              AI Auto-Analyze
            </h3>
            <div className="flex items-center space-x-2">
              <span className="text-xs font-medium text-gray-500">
                {currentPage}/{totalPages}
              </span>
              {onCancel && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={onCancel}
                  className="h-6 w-6 p-0 hover:bg-gray-200"
                >
                  <X className="h-3 w-3" />
                </Button>
              )}
            </div>
          </div>

          <p className="text-sm text-gray-600 mb-3">
            {drawingName ? (
              <>Analyzing "{drawingName}" for data areas...</>
            ) : (
              <>AI is analyzing pages for data tables and schedules...</>
            )}
          </p>

          {/* Progress bar */}
          <div className="w-full bg-gray-200 rounded-full h-2 mb-2">
            <div 
              className="bg-purple-600 h-2 rounded-full transition-all duration-300 ease-out"
              style={{ width: `${progress}%` }}
            />
          </div>

          <p className="text-xs text-purple-600">
            AI is identifying potential data areas...
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/components/ai-credits-dashboa.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  CreditCard, 
  DollarSign, 
  TrendingUp, 
  AlertCircle, 
  Settings, 
  History,
  Zap,
  Brain,
  Users
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { loadStripe } from "@stripe/stripe-js";
import { ReferralDashboard } from "./referral-dashboard";

const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY);

interface CreditBalance {
  balance: number;
  monthlySpent: number;
  totalSpent: number;
  alertThreshold: number;
  autoPurchase: boolean;
  autoPurchaseAmount: number;
}

interface UsageRecord {
  id: number;
  operation: string;
  tokensUsed: number;
  cost: number;
  model: string;
  createdAt: string;
}

interface Transaction {
  id: number;
  type: string;
  amount: number;
  balance: number;
  description: string;
  createdAt: string;
}

interface PaymentMethod {
  id: string;
  brand: string;
  last4: string;
  expMonth: number;
  expYear: number;
}

export function AiCreditsDashboard() {
  const [purchaseAmount, setPurchaseAmount] = useState(20);
  const [settings, setSettings] = useState({
    creditAlertThreshold: 5.0,
    autoPurchaseCredits: false,
    autoPurchaseAmount: 20.0
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch credit balance
  const { data: balance, isLoading: balanceLoading } = useQuery<CreditBalance>({
    queryKey: ["/api/ai-credits/balance"],
  });

  // Fetch usage history
  const { data: usage = [] } = useQuery<UsageRecord[]>({
    queryKey: ["/api/ai-credits/usage"],
  });

  // Fetch transaction history
  const { data: transactions = [] } = useQuery<Transaction[]>({
    queryKey: ["/api/ai-credits/transactions"],
  });

  // Fetch payment method
  const { data: paymentMethod } = useQuery<PaymentMethod | null>({
    queryKey: ["/api/ai-credits/payment-method"],
  });

  // Purchase credits mutation
  const purchaseCredits = useMutation({
    mutationFn: async (amount: number) => {
      try {
        console.log('Purchasing credits for amount:', amount);
        const response = await apiRequest("/api/ai-credits/purchase", "POST", { amount });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || `HTTP ${response.status}: ${response.statusText}`);
        }

        const data = await response.json();
        console.log('Purchase response:', data);
        return data;
      } catch (error: any) {
        console.error('Purchase API error:', error);
        throw error;
      }
    },
    onSuccess: async (data) => {
      try {
        const stripe = await stripePromise;
        if (!stripe) {
          throw new Error("Stripe failed to load");
        }

        console.log('Redirecting to Stripe checkout with session:', data.sessionId);

        // Try Stripe's redirect method first
        try {
          const result = await stripe.redirectToCheckout({
            sessionId: data.sessionId
          });

          console.log('Stripe redirect result:', result);

          if (result.error) {
            console.error('Stripe redirect error:', result.error);
            // If Stripe redirect fails, try direct URL redirect as fallback
            if (data.url) {
              console.log('Falling back to direct URL redirect:', data.url);
              window.location.href = data.url;
              return;
            }
            throw new Error(result.error.message || "Unable to redirect to payment page");
          }
        } catch (redirectError: any) {
          console.error('Stripe redirect method failed:', redirectError);

          // Fallback: try direct URL redirect if available
          if (data.url) {
            console.log('Using fallback URL redirect:', data.url);
            window.location.href = data.url;
            return;
          }

          throw redirectError;
        }
      } catch (err: any) {
        console.error('Checkout error:', err);
        toast({
          title: "Checkout Error",
          description: err.message || "An unexpected error occurred",
          variant: "destructive",
        });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Purchase Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update settings mutation
  const updateSettings = useMutation({
    mutationFn: async (newSettings: typeof settings) => {
      const response = await apiRequest("/api/ai-credits/settings", "PUT", newSettings);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Settings Updated",
        description: "Your AI credit settings have been saved.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/ai-credits/balance"] });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed", 
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handlePurchase = () => {
    if (purchaseAmount < 5) {
      toast({
        title: "Invalid Amount",
        description: "Minimum purchase is $5.00",
        variant: "destructive",
      });
      return;
    }
    purchaseCredits.mutate(purchaseAmount);
  };

  const handleSettingsUpdate = () => {
    updateSettings.mutate(settings);
  };

  if (balanceLoading) {
    return (
      <Card>
        <CardContent className="flex items-center justify-center h-48">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
        </CardContent>
      </Card>
    );
  }

  const isLowBalance = balance && balance.balance <= balance.alertThreshold;

  return (
    <div className="space-y-6">
      {/* Credit Balance Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Available Credits</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">${balance?.balance.toFixed(2) || '0.00'}</div>
            {isLowBalance && (
