import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import type { ConstructionDivision } from "@shared/schema";

interface AnnotationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSave: (annotation: {
    divisionId: number;
    notes: string;
    priority: string;
  }) => void;
}

export default function AnnotationModal({
  isOpen,
  onClose,
  onSave,
}: AnnotationModalProps) {
  const [selectedDivisionId, setSelectedDivisionId] = useState<string>("");
  const [notes, setNotes] = useState("");
  const [priority, setPriority] = useState("medium");

  const { data: divisions } = useQuery({
    queryKey: ['/api/construction-divisions'],
  });

  const handleSave = () => {
    if (!selectedDivisionId) return;

    onSave({
      divisionId: parseInt(selectedDivisionId),
      notes,
      priority,
    });

    // Reset form
    setSelectedDivisionId("");
    setNotes("");
    setPriority("medium");
  };

  const handleClose = () => {
    onClose();
    // Reset form
    setSelectedDivisionId("");
    setNotes("");
    setPriority("medium");
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Annotation Details</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div>
            <Label htmlFor="division">Construction Division</Label>
            <Select value={selectedDivisionId} onValueChange={setSelectedDivisionId}>
              <SelectTrigger>
                <SelectValue placeholder="Select a division" />
              </SelectTrigger>
              <SelectContent>
                {divisions?.map((division) => (
                  <SelectItem key={division.id} value={division.id.toString()}>
                    <div className="flex items-center space-x-2">
                      <div
                        className="w-3 h-3 rounded-full"
                        style={{ backgroundColor: division.color }}
                      />
                      <span>{division.name}</span>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="notes">Notes</Label>
            <Textarea
              id="notes"
              placeholder="Add notes about this annotation..."
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              rows={3}
            />
          </div>

          <div>
            <Label>Priority</Label>
            <div className="flex space-x-2 mt-2">
              <Button
                variant={priority === "low" ? "default" : "outline"}
                size="sm"
                onClick={() => setPriority("low")}
                className="text-green-700 hover:bg-green-50"
              >
                Low
              </Button>
              <Button
                variant={priority === "medium" ? "default" : "outline"}
                size="sm"
                onClick={() => setPriority("medium")}
                className="text-yellow-700 hover:bg-yellow-50"
              >
                Medium
              </Button>
              <Button
                variant={priority === "high" ? "default" : "outline"}
                size="sm"
                onClick={() => setPriority("high")}
                className="text-red-700 hover:bg-red-50"
              >
                High
              </Button>
            </div>
          </div>
        </div>

        <div className="flex justify-end space-x-3 mt-6">
          <Button variant="outline" onClick={handleClose}>
            Cancel
          </Button>
          <Button 
            onClick={handleSave}
            disabled={!selectedDivisionId}
          >
            Save Annotation
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/annotation-toolbar.content:
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import type { ConstructionDivision } from "@shared/schema";
import { useState, useRef, useEffect } from "react";
import { 
  MousePointer, 
  Square, 
  Minus, 
  Plus, 
  Undo, 
  Redo,
  GripVertical
} from "lucide-react";

interface AnnotationToolbarProps {
  tool: string;
  onToolChange: (tool: string) => void;
  selectedDivision: ConstructionDivision | null;
  onDivisionChange: (division: ConstructionDivision) => void;
  zoom: number;
  onZoomChange: (zoom: number) => void;
  canUndo: boolean;
  canRedo: boolean;
  onUndo: () => void;
  onRedo: () => void;
}

export default function AnnotationToolbar({
  tool,
  onToolChange,
  selectedDivision,
  onDivisionChange,
  zoom,
  onZoomChange,
  canUndo,
  canRedo,
  onUndo,
  onRedo,
}: AnnotationToolbarProps) {
  const { data: divisions } = useQuery<ConstructionDivision[]>({
    queryKey: ['/api/construction-divisions'],
  });

  // Draggable state
  const [position, setPosition] = useState({ x: 20, y: 20 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const toolbarRef = useRef<HTMLDivElement>(null);

  const handleZoomIn = () => {
    onZoomChange(Math.min(zoom + 25, 500));
  };

  const handleZoomOut = () => {
    onZoomChange(Math.max(zoom - 25, 25));
  };

  // Drag handlers
  const handleMouseDown = (e: React.MouseEvent) => {
    if (toolbarRef.current) {
      const rect = toolbarRef.current.getBoundingClientRect();
      setDragOffset({
        x: e.clientX - position.x,
        y: e.clientY - position.y
      });
      setIsDragging(true);
    }
  };

  const handleMouseMove = (e: MouseEvent) => {
    if (isDragging) {
      // Add boundary constraints
      const newX = Math.max(0, Math.min(e.clientX - dragOffset.x, window.innerWidth - 300));
      const newY = Math.max(0, Math.min(e.clientY - dragOffset.y, window.innerHeight - 200));

      setPosition({
        x: newX,
        y: newY
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  // Effect to handle global mouse events
  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, dragOffset]);

  return (
    <div 
      ref={toolbarRef}
      className="absolute z-30 toolbar-floating rounded-lg shadow-lg border border-gray-200 p-3 select-none"
      style={{ 
        left: `${position.x}px`, 
        top: `${position.y}px`
      }}
    >
      {/* Drag Handle */}
      <div 
        className="flex items-center justify-center w-full h-6 mb-2 cursor-grab hover:bg-gray-100 rounded transition-colors border-b border-gray-200"
        onMouseDown={handleMouseDown}
        style={{ cursor: isDragging ? 'grabbing' : 'grab' }}
        title="Drag to move toolbar"
      >
        <GripVertical className="h-4 w-4 text-gray-400" />
      </div>
      {/* Status Panel */}
      <div className="mb-3 p-2 bg-gray-50 rounded border">
        <div className="text-xs font-medium text-gray-600 mb-1">Current Tool:</div>
        <div className="text-sm font-semibold text-gray-800 capitalize">{tool}</div>
        {selectedDivision && (
          <>
            <div className="text-xs font-medium text-gray-600 mt-2 mb-1">Selected Division:</div>
            <div className="flex items-center space-x-2">
              <div 
                className="w-4 h-4 rounded border"
                style={{ backgroundColor: selectedDivision.color }}
              />
              <div className="text-sm font-semibold text-gray-800">{selectedDivision.name}</div>
            </div>
          </>
        )}
        {!selectedDivision && tool === 'marquee' && (
          <div className="text-xs text-red-600 mt-2">Select a division first</div>
        )}
      </div>

      <div className="flex items-center space-x-2">
        {/* Zoom Controls */}
        <div className="flex items-center space-x-1 pr-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={handleZoomOut}
            disabled={zoom <= 25}
          >
            <Minus className="h-4 w-4" />
          </Button>
          <span className="text-sm font-medium text-gray-700 px-2 min-w-16 text-center">
            {zoom}%
          </span>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleZoomIn}
            disabled={zoom >= 500}
          >
            <Plus className="h-4 w-4" />
          </Button>
        </div>

        <Separator orientation="vertical" className="h-6" />

        {/* Annotation Tools */}
        <div className="flex items-center space-x-1 px-2">
          <Button
            variant={tool === 'cursor' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => onToolChange('cursor')}
            title="Cursor Tool"
          >
            <MousePointer className="h-4 w-4" />
          </Button>
          <Button
            variant={tool === 'marquee' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => onToolChange('marquee')}
            title="Marquee Selection Tool"
            disabled={!selectedDivision}
          >
            <Square className="h-4 w-4" />
          </Button>
        </div>



        {/* Undo/Redo */}
        <div className="flex items-center space-x-1 pl-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={onUndo}
            disabled={!canUndo}
            title="Undo"
          >
            <Undo className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={onRedo}
            disabled={!canRedo}
            title="Redo"
          >
            <Redo className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Status Panel */}
      <div className="mt-3 p-2 bg-gray-50 rounded border">
        <div className="text-xs text-gray-600 mb-1">Current Status:</div>
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center space-x-2">
            <span className="font-medium">Tool:</span>
            <span className="capitalize text-blue-600">{tool}</span>
          </div>
          {selectedDivision ? (
            <div className="flex items-center space-x-2">
              <span className="font-medium">Division:</span>
              <div
                className="w-3 h-3 rounded-full"
                style={{ backgroundColor: selectedDivision.color }}
              />
              <span className="text-green-600">{selectedDivision.name}</span>
            </div>
          ) : (
            <span className="text-orange-600">Select a division to start</span>
          )}
        </div>
        {selectedDivision && tool !== 'pan' && (
          <div className="text-xs text-gray-500 mt-1">
            Click and drag on the drawing to {tool === 'highlight' ? 'highlight' : `draw ${tool}s`}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/background-ai-stat.content:
import { useEffect, useState } from "react";
import { Brain, CheckCircle2, Loader2 } from "lucide-react";
import { queryClient } from "@/lib/queryClient";

interface BackgroundAiStatus {
  isProcessing: boolean;
  drawingId: number | null;
  currentPage: number;
  totalPages: number;
  fileName: string;
}

export default function BackgroundAiStatus() {
  const [status, setStatus] = useState<BackgroundAiStatus>({
    isProcessing: false,
    drawingId: null,
    currentPage: 0,
    totalPages: 0,
    fileName: ''
  });

  useEffect(() => {
    const pollStatus = async () => {
      try {
        const response = await fetch('/api/background-ai/status');
        if (response.ok) {
          const data = await response.json();
          const wasProcessing = status.isProcessing;
          setStatus(data);

          // If AI processing just completed, invalidate caches to refresh thumbnails and drawing data
          if (wasProcessing && !data.isProcessing && data.drawingId) {
            queryClient.invalidateQueries({
              queryKey: ['drawings', data.drawingId, 'metadata']
            });
            // Also invalidate the main drawings query to refresh the drawing list with updated metadata
            queryClient.invalidateQueries({
              queryKey: ['/api/drawings']
            });
          }
        }
      } catch (error) {
        console.error('Failed to fetch background AI status:', error);
      }
    };

    // Poll every 1 second when processing for more responsive updates
    const interval = setInterval(pollStatus, 1000);
    pollStatus(); // Initial call

    return () => clearInterval(interval);
  }, []);

  if (!status.isProcessing) {
    return null;
  }

  const progress = status.totalPages > 0 ? Math.min(95, (status.currentPage / status.totalPages) * 100) : 0;

  return (
    <div className="fixed bottom-4 right-4 bg-white border border-gray-200 rounded-lg shadow-lg p-4 min-w-80 z-50">
      <div className="flex items-center space-x-3">
        <div className="flex-shrink-0">
          {status.isProcessing ? (
            <Brain className="h-5 w-5 text-purple-600 animate-pulse" />
          ) : (
            <CheckCircle2 className="h-5 w-5 text-green-600" />
          )}
        </div>

        <div className="flex-1">
          <div className="flex items-center justify-between mb-1">
            <h4 className="text-sm font-medium text-gray-900">
              AI Enhanced Sheet Labels
            </h4>
            <span className="text-xs text-gray-500">
              {status.currentPage}/{status.totalPages}
            </span>
          </div>

          <p className="text-xs text-gray-600 mb-2">
            Analyzing "{status.fileName}" in background
          </p>

          <div className="w-full bg-gray-200 rounded-full h-1.5">
            <div 
              className="bg-purple-600 h-1.5 rounded-full transition-all duration-300"
              style={{ width: `${progress}%` }}
            />
          </div>

          <p className="text-xs text-purple-600 mt-1">
            AI is extracting sheet numbers and titles...
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/components/batch-extraction-p.content:
import React from 'react';
import { Bot, Zap } from 'lucide-react';

interface BatchExtractionProgressProps {
  isVisible: boolean;
  extractedCount: number;
  totalCount: number;
  currentArea?: string;
}

export default function BatchExtractionProgress({
  isVisible,
  extractedCount,
  totalCount,
  currentArea
}: BatchExtractionProgressProps) {
  if (!isVisible) return null;

  const progress = totalCount > 0 ? (extractedCount / totalCount) * 100 : 0;

  return (
    <div className="fixed bottom-4 right-4 z-50 bg-white rounded-lg shadow-lg border border-gray-200 p-4 w-80 animate-in slide-in-from-bottom-2">
      <div className="flex items-start space-x-3">
        <div className="flex-shrink-0">
          <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
            <Zap className="w-4 h-4 text-blue-600" />
          </div>
        </div>

        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between mb-1">
            <h3 className="text-sm font-semibold text-gray-900">
              Batch Data Extraction
            </h3>
            <span className="text-xs font-medium text-gray-500">
              {extractedCount}/{totalCount}
            </span>
          </div>

          <p className="text-sm text-gray-600 mb-3">
            {currentArea ? (
              <>AI is extracting data from selected areas...</>
            ) : (
              <>Processing highlighted areas with AI analysis...</>
            )}
          </p>

          {/* Progress bar */}
          <div className="w-full bg-gray-200 rounded-full h-2 mb-2">
            <div 
              className="bg-blue-600 h-2 rounded-full transition-all duration-300 ease-out"
              style={{ width: `${progress}%` }}
            />
          </div>

          {currentArea && (
            <p className="text-xs text-gray-500 truncate">
              Current: {currentArea}
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/column-manager.tsx.content:
import React, { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Trash2, GripVertical, Plus } from "lucide-react";
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';

interface TemplateColumn {
  name: string;
  type: 'text' | 'auto' | 'number' | 'date' | 'dimension' | 'boolean';
  description?: string;
  required?: boolean;
  example?: string;
}

interface ColumnManagerProps {
  columns: TemplateColumn[];
  onUpdateColumn: (columnIndex: number, updates: Partial<TemplateColumn>) => void;
  onDeleteColumn: (columnIndex: number) => void;
  onReorderColumns: (reorderedColumns: TemplateColumn[]) => void;
  onAddColumn: (column: TemplateColumn) => void;
  onClose: () => void;
}

export default function ColumnManager({ columns, onUpdateColumn, onDeleteColumn, onReorderColumns, onAddColumn, onClose }: ColumnManagerProps) {
  const [newColumnName, setNewColumnName] = useState('');
  const [newColumnType, setNewColumnType] = useState<'text' | 'auto' | 'number' | 'date' | 'dimension' | 'boolean'>('text');
  const [isAddingColumn, setIsAddingColumn] = useState(false);
  const [editingColumnIndex, setEditingColumnIndex] = useState<number | null>(null);
  const [editingName, setEditingName] = useState('');

  const handleDragEnd = (result: any) => {
    if (!result.destination) {
      return;
    }

    const reorderedColumns = Array.from(columns);
    const [reorderedItem] = reorderedColumns.splice(result.source.index, 1);
    reorderedColumns.splice(result.destination.index, 0, reorderedItem);

    onReorderColumns(reorderedColumns);
  };

  const handleAddColumn = async () => {
    if (!newColumnName.trim()) return;

    setIsAddingColumn(true);
    try {
      const newColumn: TemplateColumn = {
        name: newColumnName.trim(),
        type: newColumnType,
        description: `User-added ${newColumnType} column`,
        required: false,
        example: ''
      };

      await onAddColumn(newColumn);
      setNewColumnName('');
      setNewColumnType('text');
    } catch (error) {
      console.error('Failed to add column:', error);
    } finally {
      setIsAddingColumn(false);
    }
  };

  const handleEditColumn = (index: number) => {
    setEditingColumnIndex(index);
    setEditingName(columns[index].name);
  };

  const handleSaveEdit = (index: number) => {
    if (editingName.trim()) {
      onUpdateColumn(index, { name: editingName.trim() });
    }
    setEditingColumnIndex(null);
    setEditingName('');
  };

  const handleCancelEdit = () => {
    setEditingColumnIndex(null);
    setEditingName('');
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold">Manage Columns</h3>
      </div>

      {/* Add New Column Form */}
      <div className="p-3 border-2 border-dashed border-gray-200 rounded-lg bg-gray-50">
        <div className="flex items-center space-x-2 mb-2">
          <Plus className="h-4 w-4 text-gray-400" />
          <span className="text-sm font-medium text-gray-600">Add New Column</span>
        </div>
        <div className="flex items-center space-x-2">
          <Input
            placeholder="Enter column name..."
            value={newColumnName}
            onChange={(e) => setNewColumnName(e.target.value)}
            className="flex-1"
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                handleAddColumn();
              }
            }}
            disabled={isAddingColumn}
          />
          <Select value={newColumnType} onValueChange={(value: any) => setNewColumnType(value)}>
            <SelectTrigger className="w-24">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="text">text</SelectItem>
              <SelectItem value="auto">auto</SelectItem>
              <SelectItem value="number">number</SelectItem>
              <SelectItem value="date">date</SelectItem>
              <SelectItem value="dimension">dimension</SelectItem>
              <SelectItem value="boolean">boolean</SelectItem>
            </SelectContent>
          </Select>
          <Button 
            onClick={handleAddColumn} 
            disabled={!newColumnName.trim() || isAddingColumn}
            size="sm"
          >
            {isAddingColumn ? 'Adding...' : 'Add'}
          </Button>
        </div>
      </div>

      {/* Existing Columns with Drag & Drop */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="columns">
          {(provided) => (
            <div 
              {...provided.droppableProps} 
              ref={provided.innerRef}
              className="space-y-2 max-h-96 overflow-y-auto"
            >
              {columns.map((column, index) => (
                <Draggable key={column.name} draggableId={column.name} index={index}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      className={`grid grid-cols-12 gap-4 items-center p-4 rounded-lg border transition-colors ${
                        snapshot.isDragging ? 'bg-blue-50 border-blue-200 shadow-lg' : 'bg-white border-gray-200 hover:bg-gray-50'
                      }`}
                    >
                      {/* Drag Handle */}
                      <div className="col-span-1 flex justify-center">
                        <div 
                          {...provided.dragHandleProps}
                          className="text-gray-400 hover:text-gray-600 cursor-grab active:cursor-grabbing"
                        >
                          <GripVertical className="h-4 w-4" />
                        </div>
                      </div>

                      {/* Column Name */}
                      <div className="col-span-3">
                        {editingColumnIndex === index ? (
                          <div className="flex items-center space-x-2">
                            <Input
                              value={editingName}
                              onChange={(e) => setEditingName(e.target.value)}
                              className="flex-1 text-sm font-medium h-8 px-3 border border-blue-300 bg-blue-50 focus:border-blue-500 focus:ring-2 focus:ring-blue-200"
                              onKeyDown={(e) => {
                                if (e.key === 'Enter') {
                                  handleSaveEdit(index);
                                } else if (e.key === 'Escape') {
                                  handleCancelEdit();
                                }
                              }}
                              autoFocus
                              placeholder="Column name"
                            />
                            {column.required && <span className="text-red-500 text-sm">*</span>}
                          </div>
                        ) : (
                          <div className="flex items-center space-x-2">
                            <span 
                              className="font-medium text-sm cursor-pointer hover:text-blue-600 truncate" 
                              onClick={() => handleEditColumn(index)}
                              title="Click to edit"
                            >
                              {column.name}
                            </span>
                            {column.required && <span className="text-red-500 text-sm">*</span>}
                          </div>
                        )}
                      </div>

                      {/* Type Badge */}
                      <div className="col-span-2">
                        {editingColumnIndex === index ? (
                          <Select 
                            value={column.type} 
                            onValueChange={(value: any) => onUpdateColumn(index, { type: value })}
                          >
                            <SelectTrigger className="w-full h-8 text-sm border border-blue-300 bg-blue-50 focus:border-blue-500">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="text">text</SelectItem>
                              <SelectItem value="auto">auto</SelectItem>
                              <SelectItem value="number">number</SelectItem>
                              <SelectItem value="date">date</SelectItem>
                              <SelectItem value="dimension">dimension</SelectItem>
                              <SelectItem value="boolean">boolean</SelectItem>
                            </SelectContent>
                          </Select>
                        ) : (
                          <Badge 
                            variant="outline" 
                            className="text-sm h-7 px-3 cursor-pointer hover:bg-gray-100 w-full justify-center"
                            onClick={() => handleEditColumn(index)}
                            title="Click to edit"
                          >
                            {column.type}
                          </Badge>
                        )}
                      </div>

                      {/* Edit Buttons (only show when editing) */}
                      <div className="col-span-2">
                        {editingColumnIndex === index ? (
                          <div className="flex items-center space-x-2">
                            <button 
                              onClick={() => handleSaveEdit(index)} 
                              className="h-8 w-8 rounded text-white bg-green-600 hover:bg-green-700 flex items-center justify-center text-sm transition-colors"
                              title="Save changes"
                            >
                              ‚úì
                            </button>
                            <button 
                              onClick={handleCancelEdit} 
                              className="h-8 w-8 rounded text-gray-600 bg-gray-200 hover:bg-gray-300 flex items-center justify-center text-sm transition-colors"
                              title="Cancel"
                            >
                              ‚úï
                            </button>
                          </div>
                        ) : null}
                      </div>

                      {/* Required Checkbox */}
                      <div className="col-span-3">
                        <div className="flex items-center space-x-2">
                          <Checkbox
                            id={`required-${index}`}
                            checked={column.required || false}
                            onCheckedChange={(checked) => 
                              onUpdateColumn(index, { required: checked as boolean })
                            }
                            className="h-4 w-4"
                          />
                          <label 
                            htmlFor={`required-${index}`}
                            className="text-sm text-gray-600 cursor-pointer whitespace-nowrap"
                          >
                            Required
                          </label>
                        </div>
                      </div>

                      {/* Delete Button */}
                      <div className="col-span-1 flex justify-center">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onDeleteColumn(index)}
                          className="h-8 w-8 p-0 text-red-600 hover:text-red-700 hover:bg-red-50"
                          title="Delete Column"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

client/src/components/contact-sales-moda.content:
import { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Card, CardContent } from '@/components/ui/card';
import { X, Mail, Phone, Building, User, MessageSquare, CheckCircle } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface ContactSalesModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface ContactFormData {
  name: string;
  company: string;
  email: string;
  phone: string;
  message: string;
}

export default function ContactSalesModal({ isOpen, onClose }: ContactSalesModalProps) {
  const { toast } = useToast();
  const [formData, setFormData] = useState<ContactFormData>({
    name: '',
    company: '',
    email: '',
    phone: '',
    message: ''
  });
  const [submitted, setSubmitted] = useState(false);

  const contactMutation = useMutation({
    mutationFn: async (data: ContactFormData) => {
      return await apiRequest('/api/contact-sales', 'POST', data);
    },
    onSuccess: () => {
      setSubmitted(true);
      toast({
        title: "Request Sent Successfully!",
        description: "Our sales team will contact you within 24 hours.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Send Request",
        description: error.message || "Please try again or contact us directly.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Basic validation
    if (!formData.name || !formData.email || !formData.company) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields (name, email, and company).",
        variant: "destructive",
      });
      return;
    }

    contactMutation.mutate(formData);
  };

  const handleInputChange = (field: keyof ContactFormData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleClose = () => {
    setSubmitted(false);
    setFormData({
      name: '',
      company: '',
      email: '',
      phone: '',
      message: ''
    });
    onClose();
  };

  if (submitted) {
    return (
      <Dialog open={isOpen} onOpenChange={handleClose}>
        <DialogContent className="max-w-md">
          <div className="text-center py-6">
            <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
              <CheckCircle className="w-8 h-8 text-green-600" />
            </div>
            <h3 className="text-lg font-semibold mb-2">Thank You!</h3>
            <p className="text-gray-600 mb-4">
              We've received your request for more information about Koncurent Pro. 
              Our sales team will contact you within 24 hours.
            </p>
            <Button onClick={handleClose} className="w-full">
              Close
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Mail className="w-5 h-5 text-blue-600" />
            Contact Sales - Koncurent Pro
          </DialogTitle>
          <DialogDescription>
            Get personalized pricing and learn more about how Koncurent Pro can help your business
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="name" className="flex items-center gap-2">
                <User className="w-4 h-4" />
                Full Name *
              </Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="John Smith"
                required
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="company" className="flex items-center gap-2">
                <Building className="w-4 h-4" />
                Company Name *
              </Label>
              <Input
                id="company"
                value={formData.company}
                onChange={(e) => handleInputChange('company', e.target.value)}
                placeholder="Acme Construction"
                required
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="email" className="flex items-center gap-2">
                <Mail className="w-4 h-4" />
                Email Address *
              </Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="john@acmeconstruction.com"
                required
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="phone" className="flex items-center gap-2">
                <Phone className="w-4 h-4" />
                Phone Number
              </Label>
              <Input
                id="phone"
                type="tel"
                value={formData.phone}
                onChange={(e) => handleInputChange('phone', e.target.value)}
                placeholder="(555) 123-4567"
              />
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="message" className="flex items-center gap-2">
              <MessageSquare className="w-4 h-4" />
              Message
            </Label>
            <Textarea
              id="message"
              value={formData.message}
              onChange={(e) => handleInputChange('message', e.target.value)}
              placeholder="Tell us about your needs, team size, or any specific questions about Koncurent Pro..."
              rows={4}
            />
          </div>

          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="pt-4">
              <div className="text-sm text-blue-800">
                <strong>What happens next?</strong>
                <ul className="mt-2 space-y-1 list-disc list-inside">
                  <li>Our sales team will contact you within 24 hours</li>
                  <li>We'll discuss your specific needs and usage requirements</li>
                  <li>You'll receive a personalized quote and demo</li>
                  <li>No commitment required - just information</li>
                </ul>
              </div>
            </CardContent>
          </Card>

          <div className="flex gap-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              className="flex-1"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={contactMutation.isPending}
              className="flex-1"
            >
              {contactMutation.isPending ? "Sending..." : "Send Request"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

