import { useRef, useEffect, useState } from 'react';
import { Drawing, ConstructionDivision } from '@shared/schema';
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";

interface DataExtractionCanvasProps {
  drawing: Drawing;
  selectedDivision: ConstructionDivision | null;
  onDataExtracted: (extractedData: {
    type: string;
    sourceLocation: string;
    data: string;
  }) => void;
  triggerClearMarquees?: number;
}

interface MarqueeSelection {
  x: number;
  y: number;
  width: number;
  height: number;
  isSelecting: boolean;
}

export default function DataExtractionCanvas({
  drawing,
  selectedDivision,
  onDataExtracted,
  triggerClearMarquees,
}: DataExtractionCanvasProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const imageRef = useRef<HTMLImageElement>(null);
  const { toast } = useToast();
  const [marquee, setMarquee] = useState<MarqueeSelection>({
    x: 0,
    y: 0,
    width: 0,
    height: 0,
    isSelecting: false,
  });
  const [isDrawing, setIsDrawing] = useState(false);
  const [startPoint, setStartPoint] = useState({ x: 0, y: 0 });

  // Debug: Log when selectedDivision changes
  useEffect(() => {
    console.log('=== DataExtractionCanvas rendered ===');
    console.log('selectedDivision:', selectedDivision);
    console.log('drawing:', drawing);
    console.log('Canvas element:', canvasRef.current);
    console.log('Image element:', imageRef.current);
  }, [selectedDivision, drawing]);

  // Force a console log on component mount
  useEffect(() => {
    console.log('=== DataExtractionCanvas MOUNTED ===');
  }, []);

  // Handle clearing marquees when data is deleted
  useEffect(() => {
    if (triggerClearMarquees && triggerClearMarquees > 0) {
      console.log('Clearing marquee selection in DataExtractionCanvas');
      setMarquee({
        x: 0,
        y: 0,
        width: 0,
        height: 0,
        isSelecting: false,
      });
      setIsDrawing(false);
    }
  }, [triggerClearMarquees]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const image = imageRef.current;

    if (!canvas || !image) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size to match image
    canvas.width = image.naturalWidth;
    canvas.height = image.naturalHeight;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw marquee selection if active
    if (marquee.isSelecting || marquee.width > 0) {
      ctx.strokeStyle = selectedDivision?.color || '#0066cc';
      ctx.fillStyle = `${selectedDivision?.color || '#0066cc'}20`;
      ctx.lineWidth = 2;
      ctx.setLineDash([5, 5]);

      ctx.fillRect(marquee.x, marquee.y, marquee.width, marquee.height);
      ctx.strokeRect(marquee.x, marquee.y, marquee.width, marquee.height);
    }
  }, [marquee, selectedDivision]);

  const getCanvasCoordinates = (e: React.MouseEvent) => {
    const canvas = canvasRef.current;
    const image = imageRef.current;
    if (!canvas || !image) return { x: 0, y: 0 };

    const rect = canvas.getBoundingClientRect();
    const scaleX = image.naturalWidth / rect.width;
    const scaleY = image.naturalHeight / rect.height;

    return {
      x: (e.clientX - rect.left) * scaleX,
      y: (e.clientY - rect.top) * scaleY,
    };
  };

  const handleMouseDown = (e: React.MouseEvent) => {
    console.log('=== handleMouseDown called ===');
    console.log('selectedDivision:', selectedDivision);

    if (!selectedDivision) {
      console.log('No division selected, ignoring mouse down');
      return;
    }

    const coords = getCanvasCoordinates(e);
    console.log('Mouse down coordinates:', coords);
    setStartPoint(coords);
    setIsDrawing(true);
    setMarquee({
      x: coords.x,
      y: coords.y,
      width: 0,
      height: 0,
      isSelecting: true,
    });
    console.log('Drawing state set to true, marquee initialized');
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDrawing || !selectedDivision) return;

    const coords = getCanvasCoordinates(e);
    const width = coords.x - startPoint.x;
    const height = coords.y - startPoint.y;

    setMarquee({
      x: width < 0 ? coords.x : startPoint.x,
      y: height < 0 ? coords.y : startPoint.y,
      width: Math.abs(width),
      height: Math.abs(height),
      isSelecting: true,
    });
  };

  const handleMouseUp = async () => {
    console.log('=== handleMouseUp called ===');
    console.log('isDrawing:', isDrawing);
    console.log('selectedDivision:', selectedDivision);
    console.log('marquee:', marquee);

    if (!isDrawing || !selectedDivision) {
      console.log('Early return: missing drawing state or division');
      return;
    }

    setIsDrawing(false);

    // Only proceed if we have a meaningful selection
    if (marquee.width > 10 && marquee.height > 10) {
      console.log('=== CREDIT CHECK TRIGGERED ===');
      console.log('Marquee size sufficient, checking credits...');

      // Check AI credits immediately when marquee selection is completed
      try {
        const response = await fetch('/api/ai-credits/balance');
        if (response.ok) {
          const creditData = await response.json();

          // If balance is zero or very low, show the credit alert immediately
          if (creditData.balance <= 0) {
            toast({
              title: "Insufficient AI Credits",
              description: "You need more AI credits to extract data. Click to purchase credits.",
              variant: "destructive",
              action: (
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={() => window.location.href = '/ai-credits'}
                >
                  Buy Credits
                </Button>
              ),
            });

            // Clear selection immediately since we can't proceed
            setMarquee({
              x: 0,
              y: 0,
              width: 0,
              height: 0,
              isSelecting: false,
            });
            return;
          }
        }
      } catch (error) {
        console.error('Failed to check credits:', error);
      }

      // If we have credits, proceed with real extraction
      try {
        const currentPage = 1; // Default to page 1 for now

        console.log('Starting real data extraction...', {
          drawingId: drawing.id,
          page: currentPage,
          region: {
            x: marquee.x,
            y: marquee.y,
            width: marquee.width,
            height: marquee.height,
          },
          division: selectedDivision.name
        });

        toast({
          title: "Extracting data...",
          description: "Processing your selection with AI",
        });

        // Call the real OCR/AI extraction API
        const response = await fetch(`/api/drawings/${drawing.id}/ocr`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            page: currentPage,
            region: {
              x: marquee.x,
              y: marquee.y,
              width: marquee.width,
              height: marquee.height,
            },
            divisionId: selectedDivision.id
          }),
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || 'Extraction failed');
        }

        const result = await response.json();
        console.log('Extraction result from API:', result);

        // Format the real extracted data
        const extractedData = {
          type: 'text', // Always text for now, could use result.extractionMethod if needed
          sourceLocation: JSON.stringify({
            x: marquee.x,
            y: marquee.y,
            width: marquee.width,
            height: marquee.height,
            page: currentPage,
          }),
          data: result.text || '', // Use the text field from API response
          confidence: result.confidence || 0,
          divisionId: selectedDivision.id
        };

        console.log('Formatted extracted data before onDataExtracted:', extractedData);
        onDataExtracted(extractedData);
        console.log('onDataExtracted called successfully');

        toast({
          title: "Data extracted successfully",
          description: `Extracted ${result.text?.length || 0} characters with ${Math.round((result.confidence || 0) * 100)}% confidence`,
        });

      } catch (error) {
        console.error('Extraction failed:', error);
        toast({
          title: "Extraction failed",
          description: error instanceof Error ? error.message : 'Failed to extract data from selection',
          variant: "destructive",
        });
      }
    }

    // Clear selection after extraction
    setTimeout(() => {
      setMarquee({
        x: 0,
        y: 0,
        width: 0,
        height: 0,
        isSelecting: false,
      });
    }, 1000);
  };

  const getFileUrl = () => {
    // For extracted PDF pages, use the processed page image
    if (drawing.pageNumber && drawing.pageNumber > 1) {
      return `/uploads/pages/page.${drawing.pageNumber}.png`;
    }
    // For original files, use the standard file endpoint
    return `/api/drawings/${drawing.id}/file`;
  };

  return (
    <div className="relative w-full h-full bg-gray-100 rounded-lg overflow-hidden">
      <div className="relative w-full h-full">
        {/* Image */}
        <img
          ref={imageRef}
          src={getFileUrl()}
          alt={drawing.name}
          className="w-full h-full object-contain"
          onLoad={() => {
            // Trigger canvas redraw when image loads
            setMarquee(prev => ({ ...prev }));
          }}
        />

        {/* Canvas overlay for marquee selection */}
        <canvas
          ref={canvasRef}
          className="absolute inset-0 w-full h-full cursor-crosshair"
          style={{ 
            pointerEvents: selectedDivision ? 'auto' : 'none',
            opacity: selectedDivision ? 1 : 0.5,
          }}
          onClick={() => console.log('=== CANVAS CLICKED ===', { selectedDivision })}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
        />

        {/* Instruction overlay */}
        {!selectedDivision && (
          <div className="absolute inset-0 bg-black bg-opacity-20 flex items-center justify-center">
            <div className="bg-white p-4 rounded-lg shadow-lg text-center">
              <h3 className="text-lg font-semibold mb-2">Select a Construction Division</h3>
              <p className="text-gray-600">
                Choose a division from the left sidebar to start extracting data
              </p>
            </div>
          </div>
        )}

        {/* Selection status */}
        {selectedDivision && (
          <div className="absolute top-4 left-4 bg-white p-2 rounded-lg shadow-lg">
            <div className="flex items-center space-x-2">
              <div 
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: selectedDivision.color }}
              />
              <span className="text-sm font-medium">{selectedDivision.name}</span>
            </div>
            <p className="text-xs text-gray-600 mt-1">
              Click and drag to select data area
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/data-extraction-si.content:
import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { ScrollArea } from "@/components/ui/scroll-area";
import { CloudUpload, FileImage, Loader2, Calendar, Table, ChevronDown, ChevronRight, Trash2, Check } from "lucide-react";
import { Project, Drawing, ConstructionDivision } from "@shared/schema";
import UploadArea from "@/components/upload-area";
import DataTable from "@/components/data-table";
import { formatFileSize } from "@/lib/file-utils";
import kLogo from "@assets/Hubspot Scheduler Logo Image (1)_1751563530272.png";

interface DataExtractionSidebarProps {
  projects: Project[];
  drawings: Drawing[];
  currentProject: Project | null;
  currentDrawing: Drawing | null;
  onProjectSelect: (project: Project) => void;
  onDrawingSelect: (drawing: Drawing) => void;
  onDrawingDelete?: (drawingId: number) => void;
  onFileUpload: (files: File[]) => void;
  isUploading: boolean;
  constructionDivisions: ConstructionDivision[];
  selectedDivision: ConstructionDivision | null;
  onDivisionSelect: (division: ConstructionDivision) => void;
  extractedData: Record<number, Array<{
    id: string;
    type: string;
    extractedAt: Date;
    sourceLocation: string;
    items: Array<Record<string, any>>;
  }>>;
  showDataTable?: boolean;
}

export default function DataExtractionSidebar({
  projects,
  drawings,
  currentProject,
  currentDrawing,
  onProjectSelect,
  onDrawingSelect,
  onDrawingDelete,
  onFileUpload,
  isUploading,
  constructionDivisions,
  selectedDivision,
  onDivisionSelect,
  extractedData,
  showDataTable = false,
}: DataExtractionSidebarProps) {
  const [drawingsCollapsed, setDrawingsCollapsed] = useState(false);
  const [selectedDrawings, setSelectedDrawings] = useState<Set<number>>(new Set());

  return (
    <div className="h-full bg-white border-r border-gray-200 flex flex-col">
      {/* Header - Completely Rewritten */}
      <div className="px-6 py-8 border-b border-gray-200">
        <div style={{ display: 'flex', alignItems: 'flex-start', gap: '24px' }}>
          <div style={{ flexShrink: 0 }}>
            <img 
              src={kLogo} 
              alt="Koncurent Logo" 
              style={{ 
                width: '96px', 
                height: '96px',
                borderRadius: '8px',
                objectFit: 'contain'
              }}
            />
          </div>
          <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', minHeight: '96px' }}>
            <h1 style={{ 
              fontSize: '24px', 
              fontWeight: '700', 
              color: '#111827', 
              margin: '0 0 8px 0',
              lineHeight: '1.2'
            }}>
              Koncurent Hi-LYTE
            </h1>
            <p style={{ 
              fontSize: '16px', 
              color: '#6B7280', 
              margin: '0',
              lineHeight: '1.2'
            }}>
              Data Extraction Tool
            </p>
          </div>
        </div>
      </div>

      {/* Upload Section */}
      <div className="p-4 border-b border-gray-200">
        <UploadArea onFileUpload={onFileUpload} isUploading={isUploading} />
      </div>

      {/* Drawings List */}
      <div className="flex-1 flex flex-col min-h-0">
        <div className="p-4 border-b border-gray-200">
          <div className="flex items-center justify-between mb-3">
            <button
              onClick={() => setDrawingsCollapsed(!drawingsCollapsed)}
              className="flex items-center space-x-2 text-sm font-semibold text-gray-900 hover:text-gray-700"
            >
              {drawingsCollapsed ? (
                <ChevronRight className="h-4 w-4" />
              ) : (
                <ChevronDown className="h-4 w-4" />
              )}
              <span>Uploaded Drawings ({drawings.length})</span>
            </button>

            {selectedDrawings.size > 0 && !drawingsCollapsed && (
              <div className="flex items-center space-x-2">
                <span className="text-xs text-gray-500">
                  {selectedDrawings.size} selected
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    if (onDrawingDelete) {
                      selectedDrawings.forEach(id => onDrawingDelete(id));
                      setSelectedDrawings(new Set());
                    }
                  }}
                  className="h-6 px-2 text-xs text-red-600 hover:text-red-700"
                >
                  <Trash2 className="h-3 w-3 mr-1" />
                  Delete
                </Button>
              </div>
            )}
          </div>
        </div>

        {!drawingsCollapsed && (
          <div className="flex-1 px-4 overflow-y-auto">
            <div className="space-y-2 py-2">
              {drawings.length > 0 ? (
                <div className="space-y-2">
                  {drawings.map((drawing) => {
                    const isSelected = currentDrawing?.id === drawing.id;
                    const isSelectedForDeletion = selectedDrawings.has(drawing.id);

                    return (
                      <div
                        key={drawing.id}
                        className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                          isSelected 
                            ? 'bg-blue-50 border-blue-200' 
                            : 'border-gray-200 hover:bg-gray-50'
                        }`}
                      >
                        <div className="flex items-center space-x-3">
                          <button
                            onClick={(e) => {
                              e.stopPropagation();
                              const newSelected = new Set(selectedDrawings);
                              if (isSelectedForDeletion) {
                                newSelected.delete(drawing.id);
                              } else {
                                newSelected.add(drawing.id);
                              }
                              setSelectedDrawings(newSelected);
                            }}
                            className={`w-4 h-4 rounded border-2 flex items-center justify-center ${
                              isSelectedForDeletion
                                ? 'bg-blue-600 border-blue-600 text-white'
                                : 'border-gray-300 hover:border-gray-400'
                            }`}
                          >
                            {isSelectedForDeletion && <Check className="h-2 w-2" />}
                          </button>

                          <FileImage className={`h-5 w-5 ${isSelected ? 'text-blue-600' : 'text-gray-400'}`} />

                          <div 
                            className="flex-1 min-w-0"
                            onClick={() => onDrawingSelect(drawing)}
                          >
                            <div className="flex items-center justify-between">
                              <p className="text-sm font-medium text-gray-900 truncate">
                                {drawing.name}
                                {drawing.pageNumber && drawing.pageNumber > 1 && ` (Page ${drawing.pageNumber})`}
                              </p>
                              <div className="flex items-center space-x-2 text-xs text-gray-500">
                                <span>{drawing.fileType?.toUpperCase()}</span>
                                <span>‚Ä¢</span>
                                <span>{formatFileSize(drawing.fileSize)}</span>
                              </div>
                            </div>
                            <div className="flex items-center space-x-4 mt-1">
                              <div className="flex items-center space-x-1 text-xs text-gray-500">
                                <Calendar className="h-3 w-3" />
                                <span>{new Date(drawing.uploadedAt || new Date()).toLocaleDateString()}</span>
                              </div>
                              {drawing.totalPages && drawing.totalPages > 1 && (
                                <span className="text-xs text-blue-600 font-medium">
                                  {drawing.totalPages} pages total
                                </span>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <FileImage className="h-12 w-12 mx-auto mb-2 text-gray-300" />
                  <p className="text-sm">No drawings uploaded yet</p>
                  <p className="text-xs">Upload PDF files to get started</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Construction Divisions Panel */}
      <Separator />
      <div className="p-4">
        <h3 className="text-sm font-semibold text-gray-900 mb-3">Construction Divisions</h3>
        <div className="space-y-2">
          {constructionDivisions.map((division) => {
            const isSelected = selectedDivision?.id === division.id;
            return (
              <button
                key={division.id}
                onClick={() => onDivisionSelect(division)}
                className={`w-full px-4 py-3 rounded-lg border text-left transition-colors ${
                  isSelected 
                    ? 'border-blue-500 bg-blue-50' 
                    : 'border-gray-200 hover:border-gray-300'
                }`}
              >
                <div className="flex items-center space-x-3">
                  <div
                    className="w-4 h-4 rounded-full border-2 border-white"
                    style={{ backgroundColor: division.color }}
                  />
                  <div className="flex-1 min-w-0 pr-4">
                    <p className="text-sm font-medium text-gray-900 whitespace-nowrap">
                      {division.name}
                    </p>
                    <p className="text-xs text-gray-500">
                      {extractedData[division.id]?.length || 0} items extracted
                    </p>
                  </div>
                </div>
              </button>
            );
          })}
        </div>
      </div>

      {/* Data Table Panel */}
      {showDataTable && selectedDivision && (
        <>
          <Separator />
          <div className="p-4 border-t">
            <div className="flex items-center space-x-2 mb-3">
              <Table className="h-4 w-4 text-gray-600" />
              <h3 className="text-sm font-semibold text-gray-900">
                {selectedDivision.name} Data
              </h3>
            </div>
            <DataTable
              divisionName={selectedDivision.name}
              divisionColor={selectedDivision.color}
              divisionId={selectedDivision.id}
              drawingId={currentDrawing?.id}
              extractedData={extractedData[selectedDivision.id] || []}
            />
          </div>
        </>
      )}
    </div>
  );
}

import React, { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Progress } from "@/components/ui/progress";
