              const isSelected = selectedDivision?.id === division.id;
              const itemCount = extractedData[division.id]?.length || 0;
              const isSelectedForExport = selectedDivisionsForExport.has(division.id);
              const hasData = itemCount > 0;

              return (
                <div
                  key={division.id}
                  className={`w-full rounded-2xl border transition-colors ${
                    exportMode
                      ? isSelectedForExport
                        ? 'border-green-500 bg-green-50'
                        : hasData
                          ? 'border-gray-200 hover:border-green-300'
                          : 'border-gray-100 bg-gray-50'
                      : isSelected 
                        ? 'border-blue-500 bg-blue-50' 
                        : 'border-gray-200 hover:border-gray-300'
                  }`}
                >
                  <div className="flex items-start">
                    {/* Main selection button */}
                    <button
                      onClick={() => {
                        if (exportMode) {
                          if (hasData) {
                            handleDivisionSelectionForExport(division.id);
                          }
                        } else {
                          onDivisionSelect(division);
                        }
                      }}
                      onDoubleClick={() => {
                        if (!exportMode) {
                          // Double click to view extracted data for this division
                          if (itemCount > 0) {
                            onDivisionSelect(division);
                            setShowDataTable(true);
                          }
                        }
                      }}
                      className={`flex-1 px-4 py-3 text-left min-h-[3.5rem] ${
                        exportMode && !hasData ? 'cursor-not-allowed opacity-50' : 'cursor-pointer'
                      }`}
                      disabled={exportMode && !hasData}
                    >
                      <div className="flex items-start space-x-3">
                        {exportMode ? (
                          <div className="w-4 h-4 rounded border mt-0.5 flex-shrink-0 flex items-center justify-center bg-white">
                            {isSelectedForExport && (
                              <Check className="h-3 w-3 text-green-600" />
                            )}
                          </div>
                        ) : (
                          <div
                            className="w-4 h-4 rounded-full border border-white mt-0.5 flex-shrink-0"
                            style={{ backgroundColor: division.color }}
                          />
                        )}
                        <div className="flex-1 min-w-0 pr-4">
                          {editingDivisionId === division.id ? (
                            <div className="space-y-2">
                              <input
                                type="text"
                                value={editingDivisionName}
                                onChange={(e) => setEditingDivisionName(e.target.value)}
                                onKeyDown={(e) => handleDivisionKeyPress(e, division.id)}
                                onBlur={() => saveDivisionEdit(division.id)}
                                className="w-full text-xs font-medium text-gray-700 bg-white border border-blue-300 rounded px-2 py-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
                                autoFocus
                              />
                              <div className="flex items-center space-x-2">
                                <div
                                  className="w-3 h-3 rounded-full border border-gray-300 cursor-pointer"
                                  style={{ backgroundColor: editingDivisionColor }}
                                  onClick={() => setShowColorPicker(!showColorPicker)}
                                />
                                <span className="text-xs text-gray-500">Click color to change</span>
                              </div>
                            </div>
                          ) : (
                            <>
                              <p className={`text-xs font-medium leading-tight break-words ${
                                exportMode && !hasData ? 'text-gray-400' : 'text-gray-900'
                              }`}>
                                {division.name}
                              </p>
                              <p className={`text-xs mt-1 ${
                                exportMode && !hasData 
                                  ? 'text-gray-300' 
                                  : hasData 
                                    ? 'text-blue-600 font-medium' 
                                    : 'text-gray-500'
                              }`}>
                                {itemCount} items extracted
                                {exportMode && !hasData && ' (no data)'}
                              </p>
                            </>
                          )}
                        </div>
                      </div>
                    </button>

                    {/* Action Icons - show when showDivisionActions is true or for newly created divisions */}
                    {!exportMode && (showDivisionActions || newlyCreatedDivisionId === division.id) && (
                      <div className="flex items-center self-center space-x-1">
                        {/* Visibility Toggle */}
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            if (onDivisionVisibilityChange && visibleDivisions) {
                              const updatedVisible = new Set(visibleDivisions);
                              if (updatedVisible.has(division.id)) {
                                updatedVisible.delete(division.id);
                              } else {
                                updatedVisible.add(division.id);
                              }
                              onDivisionVisibilityChange(updatedVisible);
                            }
                            resetDivisionAutoHideTimer();
                          }}
                          className="p-1 hover:bg-gray-100 rounded transition-colors"
                          title={visibleDivisions?.has(division.id) ? "Hide division" : "Show division"}
                          onMouseDown={resetDivisionAutoHideTimer}
                        >
                          {visibleDivisions?.has(division.id) ? (
                            <Eye className="h-3 w-3 text-gray-400" />
                          ) : (
                            <EyeOff className="h-3 w-3 text-gray-400" />
                          )}
                        </button>

                        {/* Edit Division */}
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            startEditingDivision(division);
                            resetDivisionAutoHideTimer();
                          }}
                          className="p-1 hover:bg-gray-100 rounded transition-colors"
                          title="Edit division"
                          onMouseDown={resetDivisionAutoHideTimer}
                        >
                          <Edit3 className="h-3 w-3 text-gray-400" />
                        </button>

                        {/* Delete Division - only show for user-created divisions */}
                        {!division.isDefault && (
                          <button
                            onClick={(e) => {
                              e.stopPropagation();
                              setDivisionToDelete(division);
                              setDeleteDialogOpen(true);
                              resetDivisionAutoHideTimer();
                            }}
                            className="p-1 hover:bg-red-100 rounded transition-colors"
                            title="Delete division"
                            onMouseDown={resetDivisionAutoHideTimer}
                            disabled={deleteDivisionMutation.isPending}
                          >
                            <Trash2 className={`h-3 w-3 ${deleteDivisionMutation.isPending ? 'text-gray-400' : 'text-red-500'}`} />
                          </button>
                        )}
                      </div>
                    )}

                    {/* Expand button - always show in normal mode to view template/data */}
                    {!exportMode && (
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          onDivisionSelect(division);

                          // Always open data table and auto-scroll to bottom
                          setShowDataTable(true);

                          // Auto-scroll to bottom after DOM update, using requestAnimationFrame for smoothness
                          requestAnimationFrame(() => {
                            setTimeout(() => {
                              const targetPosition = document.documentElement.scrollHeight - window.innerHeight;
                              window.scrollTo({
                                top: Math.max(0, targetPosition),
                                behavior: 'smooth'
                              });
                            }, 150);
                          });
                        }}
                        className="p-2 hover:bg-gray-100 rounded-r-2xl transition-colors"
                        title={itemCount > 0 ? "View extracted data" : "View template"}
                      >
                        <ChevronDown className="h-4 w-4 text-gray-500" />
                      </button>
                    )}
                  </div>
                </div>
              );
            })}
            </div>
          </div>
        </HelpTooltip>
      </div>

      {/* Bottom Action Panel */}
      <div className="p-4 border-t border-gray-200 space-y-2">
        {/* AI Training button removed - learning happens automatically in background */}
      </div>

      {/* Create Folder Modal */}
      <CreateFolderModal
        isOpen={showCreateFolder}
        onClose={() => setShowCreateFolder(false)}
        onCreateFolder={async (folderName: string) => {
          await createFolderMutation.mutateAsync(folderName);
        }}
      />

      {/* Delete Folder Confirmation Modal */}
      <AlertDialog open={deleteConfirmOpen} onOpenChange={setDeleteConfirmOpen}>
        <AlertDialogContent className="max-w-md">
          <AlertDialogHeader>
            <AlertDialogTitle className="flex items-center gap-2 text-red-600">
              <AlertTriangle className="h-5 w-5" />
              Delete Folder
            </AlertDialogTitle>
            <AlertDialogDescription className="text-gray-600">
              Are you sure you want to delete "{folderToDelete?.name}"? All drawings in this folder will be moved to uncategorized. This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel onClick={handleCancelDelete}>
              Cancel
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={handleConfirmDelete}
              className="bg-red-600 hover:bg-red-700 focus:ring-red-600"
              disabled={deleteFolderMutation.isPending}
            >
              {deleteFolderMutation.isPending ? (
                <>
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                  Deleting...
                </>
              ) : (
                'Delete Folder'
              )}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* Delete Division Confirmation Modal */}
      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent className="max-w-md">
          <AlertDialogHeader>
            <AlertDialogTitle className="flex items-center gap-2 text-red-600">
              <AlertTriangle className="h-5 w-5" />
              Delete Division
            </AlertDialogTitle>
            <AlertDialogDescription className="text-gray-600">
              Are you sure you want to delete "{divisionToDelete?.name}"? This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel onClick={() => {
              setDeleteDialogOpen(false);
              setDivisionToDelete(null);
            }}>
              Cancel
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                if (divisionToDelete) {
                  deleteDivisionMutation.mutate(divisionToDelete.id);
                  setDeleteDialogOpen(false);
                  setDivisionToDelete(null);
                }
              }}
              className="bg-red-600 hover:bg-red-700 focus:ring-red-600"
              disabled={deleteDivisionMutation.isPending}
            >
              {deleteDivisionMutation.isPending ? (
                <>
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                  Deleting...
                </>
              ) : (
                'Delete Division'
              )}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* Delete Drawing Confirmation Modal */}
      <AlertDialog open={drawingDeleteDialogOpen} onOpenChange={setDrawingDeleteDialogOpen}>
        <AlertDialogContent className="max-w-md">
          <AlertDialogHeader>
            <AlertDialogTitle className="flex items-center gap-2 text-red-600">
              <AlertTriangle className="h-5 w-5" />
              Delete Drawing
            </AlertDialogTitle>
            <AlertDialogDescription className="text-gray-600">
              Are you sure you want to delete "{drawingToDelete?.name}"? This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel onClick={() => {
              setDrawingDeleteDialogOpen(false);
              setDrawingToDelete(null);
            }}>
              Cancel
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                if (drawingToDelete && onDrawingDelete) {
                  onDrawingDelete(drawingToDelete.id);
                  setDrawingDeleteDialogOpen(false);
                  setDrawingToDelete(null);
                }
              }}
              className="bg-red-600 hover:bg-red-700 focus:ring-red-600"
            >
              Delete Drawing
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

    </div>
  );
}

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { ScrollArea } from "@/components/ui/scroll-area";
import { CloudUpload, FileImage, Loader2, Calendar, Table, ChevronDown, ChevronRight, Trash2, Check } from "lucide-react";
import { Project, Drawing, ConstructionDivision } from "@shared/schema";
import UploadArea from "@/components/upload-area";
import DataTable from "@/components/data-table";
import { formatFileSize } from "@/lib/file-utils";
import kLogo from "@assets/Hubspot Scheduler Logo Image (1)_1751563530272.png";

interface DataExtractionSidebarProps {
  projects: Project[];
  drawings: Drawing[];
  currentProject: Project | null;
  currentDrawing: Drawing | null;
  onProjectSelect: (project: Project) => void;
  onDrawingSelect: (drawing: Drawing) => void;
  onDrawingDelete?: (drawingId: number) => void;
  onFileUpload: (files: File[]) => void;
  isUploading: boolean;
  uploadingFileName?: string;
  constructionDivisions: ConstructionDivision[];
  selectedDivision: ConstructionDivision | null;
  onDivisionSelect: (division: ConstructionDivision) => void;
  extractedData: Record<number, Array<{
    id: string;
    type: string;
    extractedAt: Date;
    sourceLocation: string;
    items: Array<Record<string, any>>;
  }>>;
  showDataTable?: boolean;
}

export default function DataExtractionSidebar({
  projects,
  drawings,
  currentProject,
  currentDrawing,
  onProjectSelect,
  onDrawingSelect,
  onDrawingDelete,
  onFileUpload,
  isUploading,
  uploadingFileName = '',
  constructionDivisions,
  selectedDivision,
  onDivisionSelect,
  extractedData,
  showDataTable = true,
}: DataExtractionSidebarProps) {
  const [uploadAreaOpen, setUploadAreaOpen] = useState(false);
  const [drawingsCollapsed, setDrawingsCollapsed] = useState(false);
  const [selectedDrawings, setSelectedDrawings] = useState<Set<number>>(new Set());

  // Group drawings by original PDF or display individually
  const groupedDrawings = new Map<number | string, Drawing[]>();

  drawings.forEach(drawing => {
    const groupKey = drawing.originalDrawingId || drawing.id;
    if (!groupedDrawings.has(groupKey)) {
      groupedDrawings.set(groupKey, []);
    }
    groupedDrawings.get(groupKey)!.push(drawing);
  });

  return (
    <div className="flex flex-col h-full">
      {/* Header */}
      <div className="p-4 border-b border-gray-200 flex-shrink-0">
        <div className="flex items-center space-x-3 mb-1">
          <img src={kLogo} alt="K" className="h-8 w-8" />
          <h2 className="text-lg font-semibold text-gray-900">Koncurent Drawing Wizard</h2>
        </div>
        <p className="text-sm text-gray-600 mt-1">Upload ‚Ä¢ Select ‚Ä¢ Extract ‚Ä¢ Organize</p>
      </div>

      {/* Upload Section */}
      <div className="p-4 border-b border-gray-200 flex-shrink-0">
        <Button
          onClick={() => setUploadAreaOpen(!uploadAreaOpen)}
          className="w-full"
          disabled={isUploading}
        >
          {isUploading ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Uploading...
            </>
          ) : (
            <>
              <CloudUpload className="mr-2 h-4 w-4" />
              Upload PDF Drawings
            </>
          )}
        </Button>

        {uploadAreaOpen && (
          <div className="mt-3">
            <UploadArea 
              onFileUpload={onFileUpload} 
              isUploading={isUploading}
              uploadingFileName={uploadingFileName}
              showProgress={isUploading && uploadingFileName.length > 0}
            />
          </div>
        )}
      </div>

      {/* Scrollable Content Area */}
      <div className="flex-1 flex flex-col min-h-0">
        {/* Drawings List */}
        <div className="p-4 border-b border-gray-200 flex-shrink-0">
          <div className="flex items-center justify-between mb-3">
            <button
              onClick={() => setDrawingsCollapsed(!drawingsCollapsed)}
              className="flex items-center space-x-2 text-sm font-semibold text-gray-900 hover:text-gray-700"
            >
              {drawingsCollapsed ? (
                <ChevronRight className="h-4 w-4" />
              ) : (
                <ChevronDown className="h-4 w-4" />
              )}
              <span>Uploaded Drawings ({drawings.length})</span>
            </button>

            {selectedDrawings.size > 0 && !drawingsCollapsed && (
              <div className="flex items-center space-x-2">
                <span className="text-xs text-gray-500">
                  {selectedDrawings.size} selected
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    if (onDrawingDelete) {
                      selectedDrawings.forEach(id => onDrawingDelete(id));
                      setSelectedDrawings(new Set());
                    }
                  }}
                  className="h-6 px-2 text-xs text-red-600 hover:text-red-700"
                >
                  <Trash2 className="h-3 w-3 mr-1" />
                  Delete
                </Button>
              </div>
            )}
          </div>
        </div>

        <ScrollArea className="flex-1">
          <div className="px-4 pb-4">
            <div className="space-y-2 py-2">
            {/* Simple drawings list */}
            {drawings.length > 0 ? (
              <div className="space-y-2">
                <div className="text-xs text-gray-500 mb-2">
                  {drawings.length} drawings found
                </div>
                {drawings.map((drawing) => {
                  const isSelected = currentDrawing?.id === drawing.id;
                  const isSelectedForDeletion = selectedDrawings.has(drawing.id);

                  return (
                    <div
                      key={drawing.id}
                      className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                        isSelected 
                          ? 'bg-blue-50 border-blue-200' 
                          : 'border-gray-200 hover:bg-gray-50'
                      }`}
                    >
                      <div className="flex items-center space-x-3">
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            const newSelected = new Set(selectedDrawings);
                            if (isSelectedForDeletion) {
                              newSelected.delete(drawing.id);
                            } else {
                              newSelected.add(drawing.id);
                            }
                            setSelectedDrawings(newSelected);
                          }}
                          className={`w-4 h-4 rounded border-2 flex items-center justify-center ${
                            isSelectedForDeletion
                              ? 'bg-blue-600 border-blue-600 text-white'
                              : 'border-gray-300 hover:border-gray-400'
                          }`}
                        >
                          {isSelectedForDeletion && <Check className="h-2 w-2" />}
                        </button>

                        <FileImage className={`h-5 w-5 ${isSelected ? 'text-blue-600' : 'text-gray-400'}`} />

                        <div 
                          className="flex-1 min-w-0"
                          onClick={() => onDrawingSelect(drawing)}
                        >
                          <div className="flex items-center justify-between">
                            <p className="text-sm font-medium text-gray-900 truncate">
                              {drawing.name}
                              {drawing.pageNumber && drawing.pageNumber > 1 && ` (Page ${drawing.pageNumber})`}
                            </p>
                            <div className="flex items-center space-x-2 text-xs text-gray-500">
                              <span>{drawing.fileType?.toUpperCase()}</span>
                              <span>‚Ä¢</span>
                              <span>{formatFileSize(drawing.fileSize)}</span>
                            </div>
                          </div>
                          <div className="flex items-center space-x-4 mt-1">
                            <div className="flex items-center space-x-1 text-xs text-gray-500">
                              <Calendar className="h-3 w-3" />
                              <span>{drawing.uploadedAt ? new Date(drawing.uploadedAt).toLocaleDateString() : 'Unknown date'}</span>
                            </div>
                            {drawing.totalPages && drawing.totalPages > 1 && (
                              <span className="text-xs text-blue-600 font-medium">
                                {drawing.totalPages} pages total
                              </span>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                <FileImage className="h-12 w-12 mx-auto mb-2 text-gray-300" />
                <p className="text-sm">No drawings uploaded yet</p>
                <p className="text-xs">Upload PDF files to get started</p>
              </div>
            )}
            </div>
          </div>
        </ScrollArea>

        {/* Construction Divisions Panel */}
        <div className="border-t border-gray-200 bg-gray-50 flex flex-col min-h-0 flex-shrink-0">
          <div className="p-4 pb-2">
            <h3 className="text-sm font-semibold text-gray-900 mb-3">Construction Divisions</h3>
          </div>
          <div className="px-4 pb-4 max-h-48 overflow-y-auto">
            <div className="space-y-2">
              {constructionDivisions.map((division) => (
                <div
                  key={division.id}
                  className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                    selectedDivision?.id === division.id
                      ? 'border-blue-500 bg-blue-100 text-blue-900'
                      : 'border-gray-200 bg-white hover:border-gray-300'
                  }`}
                  onClick={() => onDivisionSelect(division)}
                >
                  <div className="flex items-center space-x-2">
                    <div 
                      className="w-3 h-3 rounded-full"
                      style={{ backgroundColor: division.color }}
                    />
                    <span className="text-xs font-medium truncate">{division.name}</span>
                  </div>

                  {/* Show extracted data count */}
                  {extractedData[division.id] && extractedData[division.id].length > 0 && (
                    <div className="mt-1 flex items-center space-x-1">
                      <Table className="h-3 w-3" />
                      <span className="text-xs text-gray-600">
                        {extractedData[division.id].length} item(s)
                      </span>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>

          {/* Data Table Panel - Shows when division is selected */}
          {showDataTable && selectedDivision && (
            <div className="border-t border-gray-200 bg-white">
              <div className="p-4">
                <div className="flex items-center space-x-2 mb-3">
                  <div 
                    className="w-3 h-3 rounded-full"
                    style={{ backgroundColor: selectedDivision.color }}
                  />
                  <h4 className="text-sm font-semibold text-gray-900">
                    {selectedDivision.name} Data
                  </h4>
                </div>

                {extractedData[selectedDivision.id] && extractedData[selectedDivision.id].length > 0 ? (
                  <div className="max-h-64 overflow-hidden">
                    <DataTable
                      divisionName={selectedDivision.name}
                      divisionColor={selectedDivision.color}
                      divisionId={selectedDivision.id}
                      drawingId={currentDrawing?.id}
                      extractedData={extractedData[selectedDivision.id] as any}
                    />
                  </div>
                ) : (
                  <div className="text-center py-6 text-gray-500">
                    <Table className="h-8 w-8 mx-auto mb-2 text-gray-300" />
                    <p className="text-xs">No data extracted yet</p>
                    <p className="text-xs">Select an area on the drawing to extract data</p>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

