                          {Object.entries(userProfileData.statistics.projectTypes).map(([type, count]: [string, any]) => (
                            <div key={type} className="flex justify-between text-xs">
                              <span className="text-gray-500">{type}</span>
                              <span className="text-teal-600">{count}</span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* File Types */}
                    {userProfileData.statistics.drawingStats?.fileTypes && Object.keys(userProfileData.statistics.drawingStats.fileTypes).length > 0 && (
                      <div className="pt-2 border-t border-teal-200">
                        <span className="text-gray-600 font-medium">File Types:</span>
                        <div className="mt-1 space-y-1">
                          {Object.entries(userProfileData.statistics.drawingStats.fileTypes).map(([type, count]: [string, any]) => (
                            <div key={type} className="flex justify-between text-xs">
                              <span className="text-gray-500">{type}</span>
                              <span className="text-teal-600">{count}</span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>

                {/* Top Divisions */}
                <div className="bg-gradient-to-br from-green-50 to-green-100 rounded-xl p-4 border border-green-200">
                  <h3 className="text-lg font-semibold text-gray-700 mb-3 flex items-center gap-2">
                    <Target className="h-4 w-4 text-green-500" />
                    Top Divisions Used
                  </h3>
                  <div className="space-y-2">
                    {userProfileData.statistics.topDivisions.length > 0 ? (
                      userProfileData.statistics.topDivisions.map(([division, count]: [string, number], index: number) => (
                        <div key={division} className="flex justify-between text-sm">
                          <span className="text-gray-600">{division}</span>
                          <span className="font-medium text-green-600">{count} extractions</span>
                        </div>
                      ))
                    ) : (
                      <p className="text-sm text-gray-500">No extractions yet</p>
                    )}
                  </div>
                </div>
              </div>

              {/* Activity Details */}
              <div className="space-y-4">
                {/* Recent Transactions */}
                <div className="bg-gradient-to-br from-purple-50 to-purple-100 rounded-xl p-4 border border-purple-200">
                  <h3 className="text-lg font-semibold text-gray-700 mb-3 flex items-center gap-2">
                    <CreditCard className="h-4 w-4 text-purple-500" />
                    Recent Transactions
                  </h3>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {userProfileData.transactions.length > 0 ? (
                      userProfileData.transactions.map((transaction: any) => (
                        <div key={transaction.id} className="flex justify-between items-center text-sm border-b border-purple-200 pb-2">
                          <div className="flex flex-col">
                            <span className="text-gray-600">{transaction.type}</span>
                            <span className="text-xs text-gray-400">
                              {new Date(transaction.createdAt).toLocaleDateString()}
                            </span>
                          </div>
                          <span className={`font-medium ${transaction.amount >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {transaction.amount >= 0 ? '+' : ''}${transaction.amount.toFixed(2)}
                          </span>
                        </div>
                      ))
                    ) : (
                      <p className="text-sm text-gray-500">No transactions yet</p>
                    )}
                  </div>
                </div>

                {/* Recent Uploads */}
                <div className="bg-gradient-to-br from-amber-50 to-amber-100 rounded-xl p-4 border border-amber-200">
                  <h3 className="text-lg font-semibold text-gray-700 mb-3 flex items-center gap-2">
                    <Calendar className="h-4 w-4 text-amber-500" />
                    Recent Uploads
                  </h3>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {userProfileData.statistics.drawingStats?.recentUploads?.length > 0 ? (
                      userProfileData.statistics.drawingStats.recentUploads.map((drawing: any, index: number) => (
                        <div key={drawing.id} className="flex justify-between items-center text-sm border-b border-amber-200 pb-2">
                          <div className="flex flex-col flex-1 min-w-0">
                            <span className="text-gray-600 truncate">{drawing.fileName || 'Unknown File'}</span>
                            <span className="text-xs text-gray-400">
                              {drawing.createdAt ? new Date(drawing.createdAt).toLocaleDateString() : 'Recently'}
                            </span>
                          </div>
                          <div className="flex flex-col items-end text-right ml-2">
                            <span className="text-amber-600 font-medium text-xs">
                              {drawing.totalPages || 1} pages
                            </span>
                            <span className="text-xs text-gray-400">
                              {drawing.fileName?.split('.').pop()?.toUpperCase() || 'PDF'}
                            </span>
                          </div>
                        </div>
                      ))
                    ) : (
                      <p className="text-sm text-gray-500">No uploads yet</p>
                    )}
                  </div>
                </div>

                {/* Recent Extractions */}
                <div className="bg-gradient-to-br from-orange-50 to-orange-100 rounded-xl p-4 border border-orange-200">
                  <h3 className="text-lg font-semibold text-gray-700 mb-3 flex items-center gap-2">
                    <Activity className="h-4 w-4 text-orange-500" />
                    Recent Activity
                  </h3>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {userProfileData.statistics.recentExtractions.length > 0 ? (
                      userProfileData.statistics.recentExtractions.map((extraction: any, index: number) => (
                        <div key={extraction.id} className="flex justify-between items-center text-sm border-b border-orange-200 pb-2">
                          <div className="flex flex-col">
                            <span className="text-gray-600">Division {extraction.divisionId}</span>
                            <span className="text-xs text-gray-400">
                              {extraction.extractedAt ? new Date(extraction.extractedAt).toLocaleDateString() : 'Recently'}
                            </span>
                          </div>
                          <span className="text-orange-600 font-medium">
                            {extraction.data?.length || 0} items
                          </span>
                        </div>
                      ))
                    ) : (
                      <p className="text-sm text-gray-500">No recent activity</p>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}

          {!userProfileData && (
            <div className="flex items-center justify-center p-8">
              <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/utils/accessControl.ts.content:
// Access control utilities for Koncurent Hi-LYTE

export interface User {
  id: string;
  email?: string;
  firstName?: string;
  lastName?: string;
}

// Admin users with full access to AI Dashboard and administrative features
const ADMIN_EMAILS = [
  'mohamed@koncurent.com',
  'ryan@koncurent.com', 
  'kevin@koncurent.com'
];

// Check if user has a Koncurent email address
export function isKoncurrentUser(user: User | null): boolean {
  if (!user?.email) return false;
  return user.email.endsWith('@koncurent.com');
}

// Check if user is a Koncurent admin
export function isKoncurrentAdmin(user: User | null): boolean {
  if (!user?.email) return false;
  return ADMIN_EMAILS.includes(user.email.toLowerCase());
}

// Check if user can access AI Dashboard
export function canAccessAIDashboard(user: User | null): boolean {
  return isKoncurrentUser(user);
}

// Check if user can access admin features
export function canAccessAdminFeatures(user: User | null): boolean {
  return isKoncurrentAdmin(user);
}

// Get user access level
export function getUserAccessLevel(user: User | null): 'admin' | 'koncurent' | 'public' {
  if (isKoncurrentAdmin(user)) return 'admin';
  if (isKoncurrentUser(user)) return 'koncurent';
  return 'public';
}

client/src/components/low-credit-warning.content:
import React, { useState, useEffect } from 'react';
import { AlertTriangle, Zap, CreditCard, X } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface LowCreditWarningProps {
  balance: number;
  threshold: number;
  autoPurchase: boolean;
  autoPurchaseAmount: number;
  onClose?: () => void;
}

export function LowCreditWarning({
  balance,
  threshold,
  autoPurchase,
  autoPurchaseAmount,
  onClose
}: LowCreditWarningProps) {
  const [showDialog, setShowDialog] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showAnimation, setShowAnimation] = useState(true);
  const { toast } = useToast();

  const isLowCredit = balance <= threshold;

  useEffect(() => {
    if (isLowCredit) {
      setShowAnimation(true);
      // Auto-hide animation after 5 seconds if not interacted with
      const timer = setTimeout(() => setShowAnimation(false), 5000);
      return () => clearTimeout(timer);
    }
  }, [isLowCredit, balance]);

  const handlePurchaseCredits = async (amount: number) => {
    setIsProcessing(true);
    try {
      const response = await apiRequest('POST', '/api/ai-credits/purchase', { amount });
      const data = await response.json();

      if (data.clientSecret) {
        // In a real implementation, this would open Stripe checkout
        toast({
          title: "Purchase Initiated",
          description: `Credit purchase of $${amount} has been initiated.`,
        });
        setShowDialog(false);
        setShowAnimation(false);
        onClose?.();
      } else {
        throw new Error('Failed to create payment intent');
      }
    } catch (error) {
      console.error('Error purchasing credits:', error);
      toast({
        title: "Purchase Failed",
        description: "Failed to initiate credit purchase. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsProcessing(false);
    }
  };

  const handleAutoPurchaseToggle = async () => {
    try {
      await apiRequest('PUT', '/api/ai-credits/settings', {
        autoPurchase: !autoPurchase,
        autoPurchaseAmount
      });

      toast({
        title: autoPurchase ? "Auto-Purchase Disabled" : "Auto-Purchase Enabled",
        description: autoPurchase 
          ? "You will need to manually purchase credits when low."
          : `Credits will automatically be purchased when balance falls below $${threshold}.`,
      });
    } catch (error) {
      console.error('Error updating auto-purchase setting:', error);
      toast({
        title: "Settings Update Failed",
        description: "Failed to update auto-purchase settings.",
        variant: "destructive",
      });
    }
  };

  if (!isLowCredit) {
    return null;
  }

  return (
    <>
      {/* Animated Banner Warning */}
      {showAnimation && (
        <div className="fixed top-4 right-4 z-50 max-w-sm">
          <Alert className="border-amber-200 bg-amber-50 animate-pulse shadow-lg">
            <AlertTriangle className="h-4 w-4 text-amber-600" />
            <AlertDescription className="flex items-center justify-between">
              <div className="flex-1">
                <span className="font-medium text-amber-800">
                  Low Credits: ${balance.toFixed(2)}
                </span>
                <p className="text-sm text-amber-700 mt-1">
                  {autoPurchase 
                    ? `Auto-purchase of $${autoPurchaseAmount} will trigger soon`
                    : 'Consider purchasing more credits'
                  }
                </p>
              </div>
              <div className="flex gap-2 ml-4">
                <Button
                  size="sm"
                  variant="outline"
                  className="h-6 text-xs border-amber-300 text-amber-700 hover:bg-amber-100"
                  onClick={() => setShowDialog(true)}
                >
                  <CreditCard className="h-3 w-3 mr-1" />
                  Buy
                </Button>
                <Button
                  size="sm"
                  variant="ghost"
                  className="h-6 w-6 p-0 text-amber-600 hover:bg-amber-100"
                  onClick={() => setShowAnimation(false)}
                >
                  <X className="h-3 w-3" />
                </Button>
              </div>
            </AlertDescription>
          </Alert>
        </div>
      )}

      {/* Credit Purchase Dialog */}
      <Dialog open={showDialog} onOpenChange={setShowDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-amber-700">
              <Zap className="h-5 w-5" />
              Low Credit Warning
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div className="bg-amber-50 p-4 rounded-lg border border-amber-200">
              <p className="text-sm text-amber-800">
                Your current balance is <span className="font-bold">${balance.toFixed(2)}</span>, 
                which is below your alert threshold of <span className="font-bold">${threshold.toFixed(2)}</span>.
              </p>
            </div>

            {autoPurchase && (
              <Alert className="border-blue-200 bg-blue-50">
                <Zap className="h-4 w-4 text-blue-600" />
                <AlertDescription className="text-blue-800">
                  <span className="font-medium">Auto-Purchase Active</span>
                  <br />
                  ${autoPurchaseAmount} will be automatically purchased when your balance reaches $0.
                </AlertDescription>
              </Alert>
            )}

            <div className="space-y-3">
              <h4 className="font-medium text-gray-900">Quick Purchase Options:</h4>
              <div className="grid grid-cols-3 gap-2">
                {[10, 25, 50].map((amount) => (
                  <Button
                    key={amount}
                    variant="outline"
                    size="sm"
                    disabled={isProcessing}
                    onClick={() => handlePurchaseCredits(amount)}
                    className="hover:bg-blue-50 hover:border-blue-300"
                  >
                    ${amount}
                  </Button>
                ))}
              </div>
            </div>

            <div className="border-t pt-4 space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium">Auto-Purchase</span>
                <Button
                  variant={autoPurchase ? "default" : "outline"}
                  size="sm"
                  onClick={handleAutoPurchaseToggle}
                  className={autoPurchase ? "bg-green-600 hover:bg-green-700" : ""}
                >
                  {autoPurchase ? "Enabled" : "Disabled"}
                </Button>
              </div>

              {autoPurchase && (
                <p className="text-xs text-gray-600">
                  ${autoPurchaseAmount} will be automatically purchased when balance reaches $0.
                </p>
              )}
            </div>

            <div className="flex gap-2 pt-4">
              <Button
                variant="outline"
                className="flex-1"
                onClick={() => setShowDialog(false)}
              >
                Remind Later
              </Button>
              <Button
                className="flex-1 bg-blue-600 hover:bg-blue-700"
                onClick={() => handlePurchaseCredits(autoPurchaseAmount)}
                disabled={isProcessing}
              >
                {isProcessing ? "Processing..." : `Buy $${autoPurchaseAmount}`}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/referral-dashboard.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Users, 
  DollarSign, 
  Copy, 
  Share2, 
  CheckCircle,
  Calendar,
  Gift
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface ReferralStats {
  referralCode: string;
  totalReferrals: number;
  referralCreditsEarned: number;
  referredUsers: Array<{ email: string, joinedAt: string }>;
}

export function ReferralDashboard() {
  const [copied, setCopied] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch referral stats
  const { data: stats, isLoading } = useQuery<ReferralStats>({
    queryKey: ["/api/referrals/stats"],
  });

  // Generate referral code mutation
  const generateCode = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/referrals/generate-code", {});
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/referrals/stats"] });
      toast({
        title: "Referral Code Generated",
        description: "Your unique referral code has been created successfully!",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Generation Failed",
        description: error.message || "Failed to generate referral code",
        variant: "destructive",
      });
    },
  });

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
      toast({
        title: "Copied!",
        description: "Referral link copied to clipboard",
      });
    } catch (err) {
      toast({
        title: "Copy Failed",
        description: "Unable to copy to clipboard",
        variant: "destructive",
      });
    }
  };

  const shareReferral = async () => {
    if (!stats?.referralCode) return;

    const referralUrl = `${window.location.origin}/signup?ref=${stats.referralCode}`;
    const shareData = {
      title: 'Join Hi-LYTE with my referral',
      text: 'Get $5 in AI credits when you sign up for Hi-LYTE using my referral link!',
      url: referralUrl,
    };

    if (navigator.share) {
      try {
        await navigator.share(shareData);
      } catch (err) {
        // Fallback to clipboard if share API fails
        copyToClipboard(referralUrl);
      }
    } else {
      copyToClipboard(referralUrl);
    }
  };

  if (isLoading) {
    return (
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        {[...Array(4)].map((_, i) => (
          <Card key={i} className="animate-pulse">
            <CardHeader className="pb-2">
              <div className="h-4 bg-muted rounded w-3/4"></div>
              <div className="h-8 bg-muted rounded w-1/2"></div>
            </CardHeader>
          </Card>
        ))}
      </div>
    );
  }

  const referralUrl = stats?.referralCode 
    ? `${window.location.origin}/signup?ref=${stats.referralCode}`
    : '';

  return (
    <div className="space-y-6">
      {/* Stats Overview */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Referrals</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats?.totalReferrals || 0}</div>
            <p className="text-xs text-muted-foreground">
              Friends you've invited
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Credits Earned</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              ${(stats?.referralCreditsEarned || 0).toFixed(2)}
            </div>
            <p className="text-xs text-muted-foreground">
              From successful referrals
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Bonus Per Referral</CardTitle>
            <Gift className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">$5.00</div>
            <p className="text-xs text-muted-foreground">
              Credits for each signup
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Your Referral Code</CardTitle>
            <Share2 className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-lg font-mono">
              {stats?.referralCode || (
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={() => generateCode.mutate()}
                  disabled={generateCode.isPending}
                >
                  Generate Code
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Referral Link Section */}
      {stats?.referralCode && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Share2 className="h-5 w-5" />
              Share Your Referral Link
            </CardTitle>
            <CardDescription>
              Share this link with friends to earn $5 in AI credits for each successful signup
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex gap-2">
              <Input
                readOnly
                value={referralUrl}
                className="flex-1 font-mono text-sm"
              />
              <Button
                variant="outline"
                size="icon"
                onClick={() => copyToClipboard(referralUrl)}
              >
                {copied ? (
                  <CheckCircle className="h-4 w-4 text-green-600" />
                ) : (
                  <Copy className="h-4 w-4" />
                )}
              </Button>
              <Button onClick={shareReferral}>
                <Share2 className="h-4 w-4 mr-2" />
                Share
              </Button>
            </div>

            <div className="bg-muted p-4 rounded-lg">
              <h4 className="font-medium mb-2">How it works:</h4>
              <ul className="text-sm text-muted-foreground space-y-1">
                <li>‚Ä¢ Share your referral link with friends</li>
                <li>‚Ä¢ They sign up using your link and get $5 welcome credits</li>
                <li>‚Ä¢ You earn $5 in AI credits for each successful signup</li>
                <li>‚Ä¢ Credits are automatically added to your account</li>
              </ul>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Referred Users List */}
      {stats?.referredUsers && stats.referredUsers.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Users className="h-5 w-5" />
              Your Referrals ({stats.referredUsers.length})
            </CardTitle>
            <CardDescription>
              Friends who joined using your referral link
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {stats.referredUsers.map((user, index) => (
                <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                  <div className="flex items-center gap-3">
                    <div className="w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center">
                      <Users className="h-4 w-4 text-primary" />
                    </div>
                    <div>
                      <div className="font-medium">{user.email}</div>
                      <div className="text-sm text-muted-foreground flex items-center gap-1">
                        <Calendar className="h-3 w-3" />
                        Joined {new Date(user.joinedAt).toLocaleDateString()}
                      </div>
                    </div>
                  </div>
                  <Badge variant="secondary">
                    +$5.00
                  </Badge>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Empty State */}
      {stats?.referralCode && (!stats.referredUsers || stats.referredUsers.length === 0) && (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Users className="h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-medium mb-2">No referrals yet</h3>
            <p className="text-muted-foreground text-center mb-4 max-w-md">
              Share your referral link with friends to start earning AI credits. 
              You'll earn $5 for each friend who signs up!
            </p>
            <Button onClick={shareReferral}>
              <Share2 className="h-4 w-4 mr-2" />
              Share Your Link
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

server/referral-service.ts.content:
import { storage } from './storage';
import { AiCreditService } from './ai-credit-service';

export class ReferralService {
  // Generate a unique referral code for a user
  static async generateReferralCode(userId: number): Promise<string> {
    let code: string;
    let isUnique = false;

    while (!isUnique) {
      // Generate 6-character alphanumeric code
      code = Math.random().toString(36).substring(2, 8).toUpperCase();

      // Check if code is already used
      const existingUser = await storage.getUserByReferralCode(code);
      if (!existingUser) {
        isUnique = true;
      }
    }

    // Update user with referral code
    await storage.updateUser(userId, { referralCode: code });
    return code;
  }

  // Process referral when a new user signs up with a referral code
  static async processReferral(newUserId: number, referralCode: string): Promise<boolean> {
    try {
      // Find the referrer by code
      const referrer = await storage.getUserByReferralCode(referralCode);
      if (!referrer) {
        console.log(`Invalid referral code: ${referralCode}`);
        return false;
      }

      // Prevent self-referral
      if (referrer.id === newUserId) {
        console.log('Self-referral attempted');
        return false;
      }

      // Update new user with referrer information
      await storage.updateUser(newUserId, { referredBy: referrer.id });

      // Award credits to both users
      const referralBonus = 5.0; // $5 bonus for both users

      // Award bonus to new user
      await AiCreditService.addCredits(
        newUserId,
        referralBonus,
        'referral_bonus',
        `Welcome bonus from referral code ${referralCode}`,
        `referral_new_${Date.now()}`
      );

      // Award bonus to referrer
      await AiCreditService.addCredits(
        referrer.id,
        referralBonus,
        'referral_bonus',
        `Referral bonus for inviting new user`,
        `referral_reward_${Date.now()}`
      );

      // Update referrer's referral stats
      await storage.updateUser(referrer.id, {
        totalReferrals: (referrer.totalReferrals || 0) + 1,
        referralCreditsEarned: (referrer.referralCreditsEarned || 0) + referralBonus
      });

      console.log(`Referral processed: ${referralCode} -> User ${newUserId}, Referrer ${referrer.id}`);
      return true;
    } catch (error) {
      console.error('Error processing referral:', error);
      return false;
    }
  }

  // Get referral stats for a user
  static async getReferralStats(userId: number) {
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error('User not found');
    }

    return {
      referralCode: user.referralCode,
      totalReferrals: user.totalReferrals || 0,
      referralCreditsEarned: user.referralCreditsEarned || 0,
      referredBy: user.referredBy
    };
  }
}

