import { useState, useEffect } from "react";
import { Card, CardHeader, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { ArrowLeft, ArrowRight, CheckCircle2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

// Onboarding Steps
import { WelcomeStep } from "./steps/WelcomeStep";
import { RoleGoalsStep } from "./steps/RoleGoalsStep";
import { FirstUploadStep } from "./steps/FirstUploadStep";
import { CompletionStep } from "./steps/CompletionStep";

interface OnboardingFlowProps {
  userId: number;
  onComplete: () => void;
}

export interface OnboardingData {
  role?: string;
  companySize?: string;
  primaryGoals?: string[];
  constructionTypes?: string[];
  experienceLevel?: string;
  completedFirstUpload?: boolean;
}

const ONBOARDING_STEPS = [
  { id: 1, title: "Welcome", description: "Tell us about yourself" },
  { id: 2, title: "Goals", description: "What are you looking to accomplish?" },
  { id: 3, title: "First Upload", description: "Let's extract your first document" },
  { id: 4, title: "Complete", description: "You're all set!" }
];

export default function OnboardingFlow({ userId, onComplete }: OnboardingFlowProps) {
  const [currentStep, setCurrentStep] = useState(1);
  const [onboardingData, setOnboardingData] = useState<OnboardingData>({});
  const { toast } = useToast();

  // Fetch current onboarding status
  const { data: userOnboarding } = useQuery({
    queryKey: ['/api/users/onboarding-status', userId],
  });

  // Update onboarding progress mutation
  const updateProgressMutation = useMutation({
    mutationFn: async (data: { step: number; data?: OnboardingData }) => {
      const response = await apiRequest(`/api/users/${userId}/onboarding`, "PATCH", data);
      return response.json();
    },
    onSuccess: () => {
      console.log('Onboarding progress updated');
    },
    onError: (error) => {
      toast({
        title: "Failed to save progress",
        description: "Your progress couldn't be saved. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Complete onboarding mutation
  const completeOnboardingMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest(`/api/users/${userId}/onboarding/complete`, "POST", {
        onboardingData
      });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Welcome to Hi-LYTE!",
        description: "You're all set to start extracting construction data.",
      });
      onComplete();
    },
    onError: (error) => {
      toast({
        title: "Failed to complete onboarding",
        description: "Please try again.",
        variant: "destructive",
      });
    },
  });

  // Load existing onboarding data
  useEffect(() => {
    if (userOnboarding && typeof userOnboarding === 'object') {
      const onboardingStep = (userOnboarding as any).onboardingStep;
      const onboardingData = (userOnboarding as any).onboardingData;

      setCurrentStep(onboardingStep || 1);
      setOnboardingData(onboardingData || {});
    }
  }, [userOnboarding]);

  const handleNext = () => {
    const nextStep = currentStep + 1;
    setCurrentStep(nextStep);

    // Save progress
    updateProgressMutation.mutate({
      step: nextStep,
      data: onboardingData
    });
  };

  const handlePrevious = () => {
    const prevStep = Math.max(1, currentStep - 1);
    setCurrentStep(prevStep);

    // Save progress
    updateProgressMutation.mutate({
      step: prevStep,
      data: onboardingData
    });
  };

  const handleStepComplete = (stepData: Partial<OnboardingData>) => {
    const updatedData = { ...onboardingData, ...stepData };
    setOnboardingData(updatedData);

    // If this is the last step, complete onboarding
    if (currentStep === ONBOARDING_STEPS.length) {
      completeOnboardingMutation.mutate();
    } else {
      handleNext();
    }
  };



  const progress = (currentStep / ONBOARDING_STEPS.length) * 100;

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <WelcomeStep
            onComplete={handleStepComplete}
            data={onboardingData}
          />
        );
      case 2:
        return (
          <RoleGoalsStep
            onComplete={handleStepComplete}
            data={onboardingData}
          />
        );
      case 3:
        return (
          <FirstUploadStep
            onComplete={handleStepComplete}
            data={onboardingData}
            userId={userId}
          />
        );
      case 4:
        return (
          <CompletionStep
            onComplete={() => completeOnboardingMutation.mutate()}
            data={onboardingData}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-slate-900 dark:to-slate-800 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl">
        {/* Progress Header */}
        <div className="mb-8 text-center">
          <div className="flex justify-center mb-6">
            <div className="bg-white dark:bg-slate-800 p-3 rounded-full shadow-lg">
              <div className="w-12 h-12 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-full flex items-center justify-center">
                <span className="text-white font-bold text-xl">Hi</span>
              </div>
            </div>
          </div>

          <h1 className="text-3xl font-bold text-slate-900 dark:text-white mb-2">
            Welcome to Hi-LYTE
          </h1>
          <p className="text-slate-600 dark:text-slate-400 mb-6">
            Let's get you set up for intelligent construction document extraction
          </p>

          {/* Progress Bar */}
          <div className="max-w-md mx-auto">
            <Progress value={progress} className="h-2 mb-4" />
            <div className="flex justify-between text-sm text-slate-500 dark:text-slate-400">
              {ONBOARDING_STEPS.map((step, index) => (
                <div
                  key={step.id}
                  className={`flex items-center ${
                    currentStep >= step.id
                      ? 'text-blue-600 dark:text-blue-400'
                      : 'text-slate-400 dark:text-slate-500'
                  }`}
                >
                  {currentStep > step.id ? (
                    <CheckCircle2 className="w-4 h-4 mr-1" />
                  ) : (
                    <div className={`w-4 h-4 mr-1 rounded-full border-2 ${
                      currentStep >= step.id
                        ? 'bg-blue-600 border-blue-600'
                        : 'border-slate-300'
                    }`} />
                  )}
                  <span className="hidden sm:inline">{step.title}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Main Content */}
        <Card className="shadow-xl">
          <CardContent className="p-8">
            {renderStep()}
          </CardContent>

          {/* Navigation */}
          {currentStep < ONBOARDING_STEPS.length && (
            <div className="flex justify-between items-center p-6 border-t border-slate-200 dark:border-slate-700">
              <Button
                variant="ghost"
                onClick={handlePrevious}
                disabled={currentStep === 1 || updateProgressMutation.isPending}
                className="flex items-center"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back
              </Button>

              <div className="flex items-center text-sm text-slate-500 dark:text-slate-400">
                Step {currentStep} of {ONBOARDING_STEPS.length}
              </div>
            </div>
          )}
        </Card>
      </div>
    </div>
  );
}

