import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { AlertCircle, Upload, FileText, CheckCircle2, Sparkles } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { OnboardingData } from "../OnboardingFlow";

interface FirstUploadStepProps {
  onComplete: (data: Partial<OnboardingData>) => void;
  data: OnboardingData;
  userId: number;
}

interface UploadProgress {
  phase: string;
  progress: number;
  message: string;
}

export function FirstUploadStep({ onComplete, data, userId }: FirstUploadStepProps) {
  const [dragActive, setDragActive] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<UploadProgress | null>(null);
  const [uploadComplete, setUploadComplete] = useState(false);
  const [extractedData, setExtractedData] = useState<any>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const uploadMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      // Start upload with progress tracking
      setUploadProgress({ phase: "Uploading", progress: 10, message: "Preparing your document..." });

      const response = await fetch('/api/drawings/upload', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();

      // Simulate processing phases with progress updates
      setUploadProgress({ phase: "Converting", progress: 40, message: "Converting PDF to images..." });
      await new Promise(resolve => setTimeout(resolve, 1500));

      setUploadProgress({ phase: "Analyzing", progress: 70, message: "Analyzing document structure..." });
      await new Promise(resolve => setTimeout(resolve, 1500));

      setUploadProgress({ phase: "Extracting", progress: 90, message: "Running Smart Extraction..." });
      await new Promise(resolve => setTimeout(resolve, 2000));

      setUploadProgress({ phase: "Complete", progress: 100, message: "Extraction complete!" });

      return result;
    },
    onSuccess: (result) => {
      setUploadComplete(true);
      setExtractedData(result);
      toast({
        title: "Upload Successful!",
        description: "Your document has been processed and data extracted.",
      });
    },
    onError: (error) => {
      setUploadProgress(null);
      toast({
        title: "Upload Failed",
        description: error.message || "Please try again with a valid PDF file.",
        variant: "destructive",
      });
    },
  });

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  const handleFile = (file: File) => {
    if (file.type !== 'application/pdf') {
      toast({
        title: "Invalid File Type",
        description: "Please upload a PDF file.",
        variant: "destructive",
      });
      return;
    }

    const formData = new FormData();
    formData.append('drawing', file);
    formData.append('folderId', '0'); // Default folder for onboarding

    uploadMutation.mutate(formData);
  };

  const handleSkip = () => {
    onComplete({ completedFirstUpload: false });
  };

  const handleComplete = () => {
    onComplete({ completedFirstUpload: true });
  };

  const onButtonClick = () => {
    fileInputRef.current?.click();
  };

  if (uploadComplete) {
    return (
      <div className="max-w-2xl mx-auto text-center">
        <div className="mb-6">
          <div className="w-20 h-20 bg-green-100 dark:bg-green-900 rounded-full flex items-center justify-center mx-auto mb-4">
            <CheckCircle2 className="w-10 h-10 text-green-600 dark:text-green-400" />
          </div>
          <h2 className="text-2xl font-bold text-slate-900 dark:text-white mb-3">
            Fantastic! Your first extraction is complete.
          </h2>
          <p className="text-slate-600 dark:text-slate-400">
            Hi-LYTE successfully processed your document and extracted construction data. 
            You're now ready to explore all the powerful features.
          </p>
        </div>

        {/* Results Preview */}
        <Card className="mb-6 text-left">
          <CardContent className="p-4">
            <div className="flex items-center space-x-2 mb-3">
              <Sparkles className="w-5 h-5 text-purple-600 dark:text-purple-400" />
              <h3 className="font-semibold text-slate-900 dark:text-white">
                Extraction Results
              </h3>
            </div>
            <div className="space-y-2 text-sm text-slate-600 dark:text-slate-400">
              <div className="flex justify-between">
                <span>Document processed:</span>
                <span className="font-medium">‚úì Complete</span>
              </div>
              <div className="flex justify-between">
                <span>Smart Extraction:</span>
                <span className="font-medium">‚úì AI-powered analysis</span>
              </div>
              <div className="flex justify-between">
                <span>Data ready for export:</span>
                <span className="font-medium">‚úì CSV, Excel formats</span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Button onClick={handleComplete} size="lg" className="px-8">
          Complete Setup
          <CheckCircle2 className="w-4 h-4 ml-2" />
        </Button>
      </div>
    );
  }

  if (uploadProgress) {
    return (
      <div className="max-w-2xl mx-auto text-center">
        <div className="mb-6">
