import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ArrowRight, Target, Building, Calculator, FileSpreadsheet, Clock, Users } from "lucide-react";
import { OnboardingData } from "../OnboardingFlow";

interface RoleGoalsStepProps {
  onComplete: (data: Partial<OnboardingData>) => void;
  data: OnboardingData;
}

const PRIMARY_GOALS = [
  {
    id: "material_takeoffs",
    title: "Material Takeoffs & Quantities", 
    description: "Extract material lists, quantities, and specifications from drawings",
    icon: Calculator
  },
  {
    id: "cost_estimation",
