    description: "Generate accurate cost estimates and bid proposals",
    icon: FileSpreadsheet
  },
  {
    id: "project_scheduling",
    title: "Project Scheduling",
    description: "Identify project phases and create construction schedules",
    icon: Clock
  },
  {
    id: "compliance_tracking",
    title: "Code Compliance & Standards",
    description: "Ensure drawings meet building codes and industry standards",
    icon: Target
  },
  {
    id: "team_collaboration",
    title: "Team Collaboration",
    description: "Share extracted data and insights across project teams",
    icon: Users
  },
  {
    id: "revision_management",
    title: "Drawing Revision Management",
    description: "Track changes between drawing versions and updates",
    icon: Building
  }
];

const CONSTRUCTION_TYPES = [
  "Commercial Construction",
  "Residential Construction", 
  "Industrial/Manufacturing",
  "Infrastructure/Civil",
  "Healthcare Facilities",
  "Educational Buildings",
  "Retail/Hospitality",
  "Mixed-Use Development",
  "Renovation/Remodeling"
];

const EXPERIENCE_LEVELS = [
  "New to construction document analysis",
  "Some experience with manual takeoffs",
  "Experienced with traditional methods",
  "Advanced user of construction software"
];

export function RoleGoalsStep({ onComplete, data }: RoleGoalsStepProps) {
  const [primaryGoals, setPrimaryGoals] = useState<string[]>(data.primaryGoals || []);
  const [constructionTypes, setConstructionTypes] = useState<string[]>(data.constructionTypes || []);
  const [experienceLevel, setExperienceLevel] = useState(data.experienceLevel || "");

  const handleGoalToggle = (goalId: string) => {
    setPrimaryGoals(prev => 
      prev.includes(goalId) 
        ? prev.filter(id => id !== goalId)
        : [...prev, goalId]
    );
  };

  const handleConstructionTypeToggle = (type: string) => {
    setConstructionTypes(prev => 
      prev.includes(type)
        ? prev.filter(t => t !== type)
        : [...prev, type]
    );
  };

  const handleContinue = () => {
    onComplete({
      primaryGoals,
      constructionTypes,
      experienceLevel
    });
  };

  const canContinue = primaryGoals.length > 0 && constructionTypes.length > 0 && experienceLevel;

  return (
    <div className="max-w-4xl mx-auto">
      {/* Header */}
      <div className="text-center mb-8">
        <h2 className="text-2xl font-bold text-slate-900 dark:text-white mb-3">
          What would you like to accomplish with Hi-LYTE?
        </h2>
        <p className="text-slate-600 dark:text-slate-400">
          Help us understand your goals so we can customize your experience.
        </p>
      </div>

      <div className="space-y-8">
        {/* Primary Goals */}
        <div>
          <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-4">
            What are your primary goals? (Select all that apply)
          </h3>
          <div className="grid md:grid-cols-2 gap-4">
            {PRIMARY_GOALS.map((goal) => {
              const Icon = goal.icon;
              const isSelected = primaryGoals.includes(goal.id);

              return (
                <Card 
                  key={goal.id}
                  className={`cursor-pointer transition-all ${
                    isSelected 
                      ? 'border-blue-500 bg-blue-50 dark:bg-blue-950 dark:border-blue-400' 
                      : 'border-slate-200 dark:border-slate-700 hover:border-slate-300 dark:hover:border-slate-600'
                  }`}
                  onClick={() => handleGoalToggle(goal.id)}
                >
                  <CardContent className="p-4">
                    <div className="flex items-start space-x-3">
                      <Checkbox 
                        checked={isSelected}
                        onChange={() => {}} // Handled by card click
                        className="mt-1"
                      />
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-2">
                          <Icon className={`w-5 h-5 ${
                            isSelected ? 'text-blue-600 dark:text-blue-400' : 'text-slate-500'
                          }`} />
                          <h4 className="font-semibold text-slate-900 dark:text-white">
                            {goal.title}
                          </h4>
                        </div>
                        <p className="text-sm text-slate-600 dark:text-slate-400">
                          {goal.description}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>

        {/* Construction Types */}
        <div>
          <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-4">
            What types of construction projects do you work on? (Select all that apply)
          </h3>
          <div className="grid md:grid-cols-3 gap-3">
            {CONSTRUCTION_TYPES.map((type) => {
              const isSelected = constructionTypes.includes(type);

              return (
                <Card 
                  key={type}
                  className={`cursor-pointer transition-all ${
                    isSelected 
                      ? 'border-green-500 bg-green-50 dark:bg-green-950 dark:border-green-400' 
                      : 'border-slate-200 dark:border-slate-700 hover:border-slate-300 dark:hover:border-slate-600'
                  }`}
                  onClick={() => handleConstructionTypeToggle(type)}
                >
                  <CardContent className="p-3">
                    <div className="flex items-center space-x-2">
                      <Checkbox 
                        checked={isSelected}
                        onChange={() => {}} // Handled by card click
                      />
                      <span className="text-sm font-medium text-slate-900 dark:text-white">
                        {type}
                      </span>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>

        {/* Experience Level */}
        <div>
          <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-4">
            What's your experience level with construction document analysis?
          </h3>
          <Select value={experienceLevel} onValueChange={setExperienceLevel}>
            <SelectTrigger className="h-12 max-w-md">
              <SelectValue placeholder="Select your experience level" />
            </SelectTrigger>
            <SelectContent>
              {EXPERIENCE_LEVELS.map((level) => (
                <SelectItem key={level} value={level}>
                  {level}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Continue Button */}
      <div className="mt-8 text-center">
        <Button 
          onClick={handleContinue} 
          disabled={!canContinue}
          size="lg"
          className="px-8"
        >
          Continue
          <ArrowRight className="w-4 h-4 ml-2" />
        </Button>
      </div>
    </div>
  );
}

