import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ArrowRight, Upload, Zap } from "lucide-react";
import { OnboardingData } from "../OnboardingFlow";

interface WelcomeStepProps {
  onComplete: (data: Partial<OnboardingData>) => void;
  data: OnboardingData;
}

const COMPANY_SIZES = [
  "Just me (1 person)",
  "Small team (2-10 people)",
  "Medium company (11-50 people)",
  "Large company (51-200 people)",
  "Enterprise (200+ people)"
];

const ROLES = [
  "Project Manager",
  "General Contractor", 
  "Architect",
  "Engineer",
  "Cost Estimator",
  "Construction Manager",
  "Superintendent",
  "Subcontractor",
  "Owner/Developer",
  "Quantity Surveyor",
  "Other"
];

export function WelcomeStep({ onComplete, data }: WelcomeStepProps) {
  const [role, setRole] = useState(data.role || "");
  const [companySize, setCompanySize] = useState(data.companySize || "");
  const [customRole, setCustomRole] = useState("");

  const handleContinue = () => {
    const finalRole = role === "Other" ? customRole : role;

    if (!finalRole || !companySize) {
      return; // Form validation would go here
    }

    onComplete({
      role: finalRole,
      companySize
    });
  };

  const canContinue = (role === "Other" ? customRole.trim() : role) && companySize;

  return (
    <div className="max-w-2xl mx-auto">
      {/* Header */}
      <div className="text-center mb-8">
        <h2 className="text-2xl font-bold text-slate-900 dark:text-white mb-3">
          Welcome to Hi-LYTE! Tell us about yourself.
        </h2>
        <p className="text-slate-600 dark:text-slate-400">
          Help us personalize your experience with intelligent construction document extraction.
        </p>
      </div>

      {/* Form */}
      <div className="space-y-6 mb-8">
        <div>
          <Label htmlFor="role" className="text-base font-medium mb-3 block">
            What best describes your role?
          </Label>
          <Select value={role} onValueChange={setRole}>
            <SelectTrigger className="h-12">
              <SelectValue placeholder="Select your role" />
            </SelectTrigger>
            <SelectContent>
              {ROLES.map((roleOption) => (
                <SelectItem key={roleOption} value={roleOption}>
                  {roleOption}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          {role === "Other" && (
            <div className="mt-3">
              <Input
                placeholder="Please specify your role"
                value={customRole}
                onChange={(e) => setCustomRole(e.target.value)}
                className="h-12"
              />
            </div>
          )}
        </div>

        <div>
          <Label htmlFor="companySize" className="text-base font-medium mb-3 block">
            How would you describe your company size?
          </Label>
          <Select value={companySize} onValueChange={setCompanySize}>
            <SelectTrigger className="h-12">
              <SelectValue placeholder="Select company size" />
            </SelectTrigger>
            <SelectContent>
              {COMPANY_SIZES.map((size) => (
                <SelectItem key={size} value={size}>
                  {size}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Continue Button */}
      <div className="text-center mb-8">
        <Button 
          disabled={!canContinue}
          onClick={handleContinue}
          size="lg"
          className="px-8 py-3 bg-blue-600 hover:bg-blue-700 text-white"
        >
          Continue Setup
          <ArrowRight className="w-5 h-5 ml-2" />
        </Button>
        <p className="text-sm text-slate-500 dark:text-slate-400 mt-3">
          Please fill out both fields to continue
        </p>
      </div>

      {/* Skip Option */}
      <div className="text-center">
        <Button 
          variant="ghost" 
          size="sm"
          onClick={() => onComplete({ role: "Not specified", companySize: "Not specified" })}
          className="text-slate-500 dark:text-slate-400"
        >
          Skip onboarding
        </Button>
      </div>
    </div>
  );
}

