import { useEffect } from "react";
import { useLocation } from "wouter";
import { useAuth } from "@/contexts/AuthContext";
import OnboardingFlow from "@/components/onboarding/OnboardingFlow";
import { useToast } from "@/hooks/use-toast";

export default function OnboardingPage() {
  const [, navigate] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    // If user has already completed onboarding, redirect to home
    if (user.onboardingCompleted) {
      navigate("/");
      return;
    }
  }, [user, navigate]);

  const handleOnboardingComplete = () => {
    toast({
      title: "Welcome to Hi-LYTE!",
      description: "Your account setup is complete. Let's start extracting construction data.",
    });
    navigate("/");
  };

  if (!user) {
    return null; // Will redirect to login
  }

  return (
    <OnboardingFlow 
      userId={user.id} 
      onComplete={handleOnboardingComplete}
    />
  );
}

client/src/components/ProcoreIntegration.content:
import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Loader2, ExternalLink, Building, FileText, Folder, CheckCircle2, XCircle, AlertCircle } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface ProcoreConfig {
  configured: boolean;
  environment: string;
  baseUrl: string;
  hasClientId: boolean;
  hasClientSecret: boolean;
  hasCompanyId: boolean;
}

interface ProcoreProject {
  id: number;
  name: string;
  project_number: string;
  address: string;
  city: string;
  state_code: string;
  stage: string;
  project_type: string;
}

interface ProcoreTokens {
  accessToken: string;
  expiresIn: number;
  tokenType: string;
  scope: string;
}

const ProcoreIntegration: React.FC = () => {
  const [config, setConfig] = useState<ProcoreConfig | null>(null);
  const [tokens, setTokens] = useState<ProcoreTokens | null>(null);
  const [projects, setProjects] = useState<ProcoreProject[]>([]);
  const [loading, setLoading] = useState(false);
  const [authLoading, setAuthLoading] = useState(false);
  const { toast } = useToast();

  useEffect(() => {
    fetchConfig();
  }, []);

  const fetchConfig = async () => {
    try {
      const response = await fetch('/api/procore/config');
      const data = await response.json();
      setConfig(data);
    } catch (error) {
      console.error('Error fetching Procore config:', error);
      toast({
        title: "Configuration Error",
        description: "Failed to fetch Procore configuration",
        variant: "destructive",
      });
    }
  };

  const handleAuth = async () => {
    setAuthLoading(true);
    try {
      const response = await fetch('/api/procore/auth-url');
      const data = await response.json();

      if (data.authUrl) {
        // Open Procore authorization in new window
        const authWindow = window.open(data.authUrl, 'procore-auth', 'width=600,height=700');

        // Listen for authorization code (in a real app, you'd handle the redirect properly)
        const checkAuth = setInterval(() => {
          try {
            if (authWindow?.location.href.includes('code=')) {
              const urlParams = new URLSearchParams(authWindow.location.search);
              const code = urlParams.get('code');
              if (code) {
                clearInterval(checkAuth);
                authWindow.close();
                exchangeToken(code);
              }
            }
          } catch (e) {
            // Cross-origin error, expected
          }
        }, 1000);

        // Clean up if window is closed manually
        const windowCheck = setInterval(() => {
          if (authWindow?.closed) {
            clearInterval(checkAuth);
            clearInterval(windowCheck);
            setAuthLoading(false);
          }
        }, 1000);
      }
    } catch (error) {
      console.error('Error starting Procore auth:', error);
      toast({
        title: "Authentication Error",
        description: "Failed to start Procore authentication",
        variant: "destructive",
      });
      setAuthLoading(false);
    }
  };

  const exchangeToken = async (code: string) => {
    try {
      const response = await fetch('/api/procore/exchange-token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code }),
      });

      const data = await response.json();

      if (data.success) {
        setTokens(data.tokens);
        toast({
          title: "Authentication Successful",
          description: "Successfully connected to Procore",
        });
        fetchProjects(data.tokens.accessToken);
      } else {
        throw new Error(data.error || 'Failed to exchange token');
      }
    } catch (error) {
      console.error('Error exchanging token:', error);
      toast({
        title: "Authentication Error",
        description: "Failed to complete Procore authentication",
        variant: "destructive",
      });
    } finally {
      setAuthLoading(false);
    }
  };

  const fetchProjects = async (accessToken: string) => {
    setLoading(true);
    try {
      const response = await fetch('/api/procore/projects', {
        headers: {
          'Authorization': `Bearer ${accessToken}`,
        },
      });

      if (response.ok) {
        const projectsData = await response.json();
        setProjects(projectsData);
      } else {
        throw new Error('Failed to fetch projects');
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
      toast({
        title: "Sync Error",
        description: "Failed to fetch projects from Procore",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const syncProjectData = async (projectId: number) => {
    if (!tokens) return;

    setLoading(true);
    try {
      const response = await fetch(`/api/procore/projects/${projectId}/sync`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${tokens.accessToken}`,
        },
      });

      const data = await response.json();

      if (data.success) {
        toast({
          title: "Sync Successful",
          description: `Synchronized ${data.data.drawings.length} drawings and ${data.data.submittals.length} submittals`,
        });
      } else {
        throw new Error(data.error || 'Sync failed');
      }
    } catch (error) {
      console.error('Error syncing project:', error);
      toast({
        title: "Sync Error",
        description: "Failed to sync project data",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (hasItem: boolean) => {
    return hasItem ? (
      <CheckCircle2 className="h-4 w-4 text-green-600" />
    ) : (
      <XCircle className="h-4 w-4 text-red-600" />
    );
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Building className="h-5 w-5" />
            Procore Integration
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {config ? (
            <>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">Environment:</span>
                    <Badge variant={config.environment === 'production' ? 'default' : 'secondary'}>
                      {config.environment}
                    </Badge>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">Base URL:</span>
                    <span className="text-sm text-muted-foreground">{config.baseUrl}</span>
                  </div>
                </div>
                <div className="space-y-2">
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">Client ID:</span>
                    {getStatusIcon(config.hasClientId)}
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">Client Secret:</span>
                    {getStatusIcon(config.hasClientSecret)}
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">Company ID:</span>
                    {getStatusIcon(config.hasCompanyId)}
                  </div>
                </div>
              </div>

              {!config.configured && (
                <Alert>
                  <AlertCircle className="h-4 w-4" />
                  <AlertDescription>
                    Procore API is not fully configured. Please set the required environment variables:
                    PROCORE_CLIENT_ID, PROCORE_CLIENT_SECRET, and PROCORE_COMPANY_ID.
                  </AlertDescription>
                </Alert>
              )}

              {config.configured && !tokens && (
                <div className="space-y-2">
                  <p className="text-sm text-muted-foreground">
                    Connect to Procore to sync your construction project data.
                  </p>
                  <Button onClick={handleAuth} disabled={authLoading}>
                    {authLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                    <ExternalLink className="mr-2 h-4 w-4" />
                    Connect to Procore
                  </Button>
                </div>
              )}

              {tokens && (
                <div className="space-y-4">
                  <div className="flex items-center gap-2">
                    <CheckCircle2 className="h-4 w-4 text-green-600" />
                    <span className="text-sm font-medium">Connected to Procore</span>
                  </div>

                  {projects.length > 0 && (
                    <div className="space-y-2">
                      <h4 className="font-medium">Available Projects</h4>
                      <div className="grid gap-2">
                        {projects.map((project) => (
                          <Card key={project.id} className="p-3">
                            <div className="flex items-center justify-between">
                              <div>
                                <div className="font-medium">{project.name}</div>
                                <div className="text-sm text-muted-foreground">
                                  {project.project_number} ‚Ä¢ {project.city}, {project.state_code}
                                </div>
                                <div className="text-xs text-muted-foreground">
                                  {project.project_type} ‚Ä¢ {project.stage}
                                </div>
                              </div>
                              <Button
                                size="sm"
                                onClick={() => syncProjectData(project.id)}
                                disabled={loading}
                              >
                                {loading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                                <Folder className="mr-2 h-4 w-4" />
                                Sync Data
                              </Button>
                            </div>
                          </Card>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </>
          ) : (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-6 w-6 animate-spin" />
            </div>
          )}
        </CardContent>
      </Card>

      {/* Features Overview */}
      <Card>
        <CardHeader>
          <CardTitle>Integration Features</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="flex items-start gap-3">
              <Building className="h-5 w-5 text-blue-600 mt-0.5" />
              <div>
                <h4 className="font-medium">Project Synchronization</h4>
                <p className="text-sm text-muted-foreground">
                  Sync project details, timelines, and metadata from Procore
                </p>
              </div>
            </div>
            <div className="flex items-start gap-3">
              <FileText className="h-5 w-5 text-green-600 mt-0.5" />
              <div>
                <h4 className="font-medium">Drawing Integration</h4>
                <p className="text-sm text-muted-foreground">
                  Import and sync construction drawings with revisions
                </p>
              </div>
            </div>
            <div className="flex items-start gap-3">
              <Folder className="h-5 w-5 text-purple-600 mt-0.5" />
              <div>
                <h4 className="font-medium">Submittal Tracking</h4>
                <p className="text-sm text-muted-foreground">
                  Access submittal data and status updates
                </p>
              </div>
            </div>
            <div className="flex items-start gap-3">
              <ExternalLink className="h-5 w-5 text-orange-600 mt-0.5" />
              <div>
                <h4 className="font-medium">Bidirectional Sync</h4>
                <p className="text-sm text-muted-foreground">
                  Upload extracted data back to Procore projects
                </p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default ProcoreIntegration;

