/**
 * Procore API Integration Service
 * Handles authentication and data sync with Procore construction management platform
 */

interface ProcoreConfig {
  clientId: string;
  clientSecret: string;
  redirectUri: string;
  baseUrl: string; // production: login.procore.com, sandbox: login-sandbox.procore.com
  apiBaseUrl: string; // production: api.procore.com, sandbox: sandbox.procore.com
}

interface ProcoreTokens {
  accessToken: string;
  refreshToken: string;
  expiresIn: number;
  tokenType: string;
  scope: string;
  createdAt: number;
}

interface ProcoreProject {
  id: number;
  name: string;
  project_number: string;
  address: string;
  city: string;
  state_code: string;
  zip: string;
  country_code: string;
  stage: string;
  project_type: string;
  estimated_start_date: string;
  estimated_completion_date: string;
  actual_start_date?: string;
  actual_completion_date?: string;
}

interface ProcoreDrawing {
  id: number;
  name: string;
  drawing_number: string;
  revision: string;
  drawing_date: string;
  discipline: string;
  created_at: string;
  updated_at: string;
  current_revision_id: number;
}

interface ProcoreSubmittal {
  id: number;
  number: string;
  title: string;
  specification_section: string;
  ball_in_court: string;
  status: string;
  received_date: string;
  due_date: string;
  created_at: string;
  updated_at: string;
}

export class ProcoreService {
  private config: ProcoreConfig;

  constructor() {
    this.config = {
      clientId: process.env.PROCORE_CLIENT_ID || '',
      clientSecret: process.env.PROCORE_CLIENT_SECRET || '',
      redirectUri: process.env.PROCORE_REDIRECT_URI || 'http://localhost:5000/api/procore/callback',
      baseUrl: process.env.PROCORE_ENVIRONMENT === 'production' 
        ? 'https://login.procore.com' 
        : 'https://login-sandbox.procore.com',
      apiBaseUrl: process.env.PROCORE_ENVIRONMENT === 'production'
        ? 'https://api.procore.com'
        : 'https://sandbox.procore.com'
    };
  }

  /**
   * Generate OAuth authorization URL for Procore login
   */
  getAuthorizationUrl(state?: string): string {
    const params = new URLSearchParams({
      response_type: 'code',
      client_id: this.config.clientId,
      redirect_uri: this.config.redirectUri,
      scope: 'project_read drawing_read submittal_read company_read',
      ...(state && { state })
    });

    return `${this.config.baseUrl}/oauth/authorize?${params.toString()}`;
  }

  /**
   * Exchange authorization code for access token
   */
  async exchangeCodeForToken(code: string): Promise<ProcoreTokens> {
    const response = await fetch(`${this.config.baseUrl}/oauth/token`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        grant_type: 'authorization_code',
        client_id: this.config.clientId,
        client_secret: this.config.clientSecret,
        code,
        redirect_uri: this.config.redirectUri,
      }),
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Failed to exchange code for token: ${error}`);
    }

    const data = await response.json();
    return {
      accessToken: data.access_token,
      refreshToken: data.refresh_token,
      expiresIn: data.expires_in,
      tokenType: data.token_type,
      scope: data.scope,
      createdAt: Date.now()
    };
  }

  /**
   * Refresh access token using refresh token
   */
  async refreshAccessToken(refreshToken: string): Promise<ProcoreTokens> {
    const response = await fetch(`${this.config.baseUrl}/oauth/token`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        grant_type: 'refresh_token',
        client_id: this.config.clientId,
        client_secret: this.config.clientSecret,
        refresh_token: refreshToken,
      }),
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Failed to refresh token: ${error}`);
    }

    const data = await response.json();
    return {
      accessToken: data.access_token,
      refreshToken: data.refresh_token,
      expiresIn: data.expires_in,
      tokenType: data.token_type,
      scope: data.scope,
      createdAt: Date.now()
    };
  }

  /**
   * Get token information
   */
  async getTokenInfo(accessToken: string): Promise<any> {
    const response = await fetch(`${this.config.baseUrl}/oauth/token/info`, {
      headers: {
        'Authorization': `Bearer ${accessToken}`,
      },
    });

    if (!response.ok) {
      throw new Error(`Failed to get token info: ${response.statusText}`);
    }

    return await response.json();
  }

  /**
   * Revoke access token
   */
  async revokeToken(token: string): Promise<void> {
    const response = await fetch(`${this.config.baseUrl}/oauth/revoke`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        token,
        client_id: this.config.clientId,
        client_secret: this.config.clientSecret,
      }),
    });

    if (!response.ok) {
      throw new Error(`Failed to revoke token: ${response.statusText}`);
    }
  }

  /**
   * Make authenticated API request to Procore
   */
  private async makeApiRequest(
    endpoint: string, 
    accessToken: string, 
    options: RequestInit = {}
  ): Promise<any> {
    const url = `${this.config.apiBaseUrl}/rest/v1.0${endpoint}`;

    const response = await fetch(url, {
      ...options,
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Procore-Company-Id': process.env.PROCORE_COMPANY_ID || '',
        'Content-Type': 'application/json',
        ...options.headers,
      },
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Procore API request failed: ${response.status} - ${error}`);
    }

    return await response.json();
  }

  /**
   * Get all projects from Procore
   */
  async getProjects(accessToken: string): Promise<ProcoreProject[]> {
    return await this.makeApiRequest('/projects', accessToken);
  }

  /**
   * Get specific project by ID
   */
  async getProject(accessToken: string, projectId: number): Promise<ProcoreProject> {
    return await this.makeApiRequest(`/projects/${projectId}`, accessToken);
  }

  /**
   * Get drawings from a specific project
   */
  async getProjectDrawings(accessToken: string, projectId: number): Promise<ProcoreDrawing[]> {
    return await this.makeApiRequest(`/projects/${projectId}/drawings`, accessToken);
  }

  /**
   * Get submittals from a specific project
   */
  async getProjectSubmittals(accessToken: string, projectId: number): Promise<ProcoreSubmittal[]> {
    return await this.makeApiRequest(`/projects/${projectId}/submittals`, accessToken);
  }

  /**
   * Upload drawing to Procore project
   */
  async uploadDrawing(
    accessToken: string, 
    projectId: number, 
    drawingData: {
      name: string;
      drawing_number: string;
      discipline: string;
      file: Buffer;
      filename: string;
    }
  ): Promise<any> {
    const formData = new FormData();
    formData.append('drawing[name]', drawingData.name);
    formData.append('drawing[drawing_number]', drawingData.drawing_number);
    formData.append('drawing[discipline]', drawingData.discipline);
    formData.append('drawing[file]', new Blob([drawingData.file]), drawingData.filename);

    return await this.makeApiRequest(
      `/projects/${projectId}/drawings`, 
      accessToken, 
      {
        method: 'POST',
        body: formData,
        headers: {} // Let fetch set Content-Type for FormData
      }
    );
  }

  /**
   * Sync project data from Procore to local database
   */
  async syncProjectData(accessToken: string, projectId: number): Promise<{
    projects: ProcoreProject[];
    drawings: ProcoreDrawing[];
    submittals: ProcoreSubmittal[];
  }> {
    const [project, drawings, submittals] = await Promise.all([
      this.getProject(accessToken, projectId),
      this.getProjectDrawings(accessToken, projectId),
      this.getProjectSubmittals(accessToken, projectId)
    ]);

    return {
      projects: [project],
      drawings,
      submittals
    };
  }

  /**
   * Check if tokens are configured
   */
  isConfigured(): boolean {
    return !!(this.config.clientId && this.config.clientSecret);
  }

  /**
   * Check if access token is expired
   */
  isTokenExpired(tokens: ProcoreTokens): boolean {
    const now = Date.now();
    const expiresAt = tokens.createdAt + (tokens.expiresIn * 1000);
    return now >= expiresAt;
  }

  /**
   * Get current configuration status
   */
  getConfigStatus(): {
    configured: boolean;
    environment: string;
    baseUrl: string;
    hasClientId: boolean;
    hasClientSecret: boolean;
    hasCompanyId: boolean;
  } {
    return {
      configured: this.isConfigured(),
      environment: process.env.PROCORE_ENVIRONMENT || 'sandbox',
      baseUrl: this.config.baseUrl,
      hasClientId: !!this.config.clientId,
      hasClientSecret: !!this.config.clientSecret,
      hasCompanyId: !!process.env.PROCORE_COMPANY_ID
    };
  }
}

export default ProcoreService;

server/autodesk-service.ts.content:
import express from 'express';

interface AutodeskTokenResponse {
  access_token: string;
  refresh_token?: string;
  expires_in: number;
  token_type: string;
  scope: string;
}

interface AutodeskHub {
  id: string;
  type: string;
  attributes: {
    name: string;
    accountId: string;
    region: string;
  };
}

interface AutodeskProject {
  id: string;
  type: string;
  attributes: {
    name: string;
    status: string;
    startDate?: string;
    endDate?: string;
  };
}

interface AutodeskFile {
  id: string;
  type: string;
  attributes: {
    name: string;
    displayName: string;
    createTime: string;
    lastModifiedTime: string;
    fileType: string;
    storageSize: number;
  };
}

class AutodeskConstructionCloudService {
  private clientId: string;
  private clientSecret: string;
  private redirectUri: string;
  private environment: 'production' | 'sandbox';
  private baseUrl: string;
  private oauthBaseUrl: string;

  constructor() {
    this.clientId = process.env.AUTODESK_CLIENT_ID || '';
    this.clientSecret = process.env.AUTODESK_CLIENT_SECRET || '';
    this.redirectUri = process.env.AUTODESK_REDIRECT_URI || 'http://localhost:5000/api/autodesk/callback';
    this.environment = (process.env.AUTODESK_ENVIRONMENT as 'production' | 'sandbox') || 'sandbox';

    // Autodesk uses the same endpoints for both sandbox and production
    this.baseUrl = 'https://developer.api.autodesk.com';
    this.oauthBaseUrl = 'https://developer.api.autodesk.com/authentication/v2';
  }

  // Configuration and status
  getConfig() {
    return {
      configured: !!(this.clientId && this.clientSecret),
      environment: this.environment,
      baseUrl: this.baseUrl,
      hasClientId: !!this.clientId,
      hasClientSecret: !!this.clientSecret,
      redirectUri: this.redirectUri
    };
  }

  // OAuth 2.0 Authorization URL (3-legged)
  getAuthorizationUrl(state?: string): string {
    if (!this.clientId) {
      throw new Error('Autodesk Client ID not configured');
    }

    const params = new URLSearchParams({
      response_type: 'code',
      client_id: this.clientId,
      redirect_uri: this.redirectUri,
      scope: 'data:read data:create data:write account:read viewables:read',
      state: state || 'default_state'
    });

    return `${this.oauthBaseUrl}/authorize?${params.toString()}`;
  }

  // Exchange authorization code for tokens (3-legged)
  async exchangeCodeForToken(code: string): Promise<AutodeskTokenResponse> {
    if (!this.clientId || !this.clientSecret) {
      throw new Error('Autodesk Client ID and Secret must be configured');
    }

    const credentials = Buffer.from(`${this.clientId}:${this.clientSecret}`).toString('base64');

    const body = new URLSearchParams({
      grant_type: 'authorization_code',
      code: code,
      redirect_uri: this.redirectUri
    });

    const response = await fetch(`${this.oauthBaseUrl}/token`, {
      method: 'POST',
      headers: {
        'Authorization': `Basic ${credentials}`,
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json'
      },
      body: body.toString()
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Token exchange failed: ${response.status} - ${error}`);
    }

    return await response.json();
  }

  // Get client credentials token (2-legged)
  async getClientCredentialsToken(): Promise<AutodeskTokenResponse> {
    if (!this.clientId || !this.clientSecret) {
      throw new Error('Autodesk Client ID and Secret must be configured');
    }

    const credentials = Buffer.from(`${this.clientId}:${this.clientSecret}`).toString('base64');

    const body = new URLSearchParams({
      grant_type: 'client_credentials',
      scope: 'data:read data:create data:write account:read'
    });

    const response = await fetch(`${this.oauthBaseUrl}/token`, {
      method: 'POST',
      headers: {
        'Authorization': `Basic ${credentials}`,
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json'
      },
      body: body.toString()
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Client credentials token failed: ${response.status} - ${error}`);
    }

    return await response.json();
  }

  // Refresh access token
  async refreshToken(refreshToken: string): Promise<AutodeskTokenResponse> {
    if (!this.clientId || !this.clientSecret) {
      throw new Error('Autodesk Client ID and Secret must be configured');
    }

    const credentials = Buffer.from(`${this.clientId}:${this.clientSecret}`).toString('base64');

    const body = new URLSearchParams({
      grant_type: 'refresh_token',
      refresh_token: refreshToken
    });

    const response = await fetch(`${this.oauthBaseUrl}/token`, {
      method: 'POST',
      headers: {
        'Authorization': `Basic ${credentials}`,
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json'
      },
      body: body.toString()
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Token refresh failed: ${response.status} - ${error}`);
    }

    return await response.json();
  }

  // API Calls with authentication
  private async makeApiCall(endpoint: string, accessToken: string, options: RequestInit = {}): Promise<any> {
    const response = await fetch(`${this.baseUrl}${endpoint}`, {
      ...options,
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json',
        ...options.headers
      }
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`API call failed: ${response.status} - ${error}`);
    }

    return await response.json();
  }

  // Get user profile
  async getUserProfile(accessToken: string): Promise<any> {
    return this.makeApiCall('/userprofile/v1/users/@me', accessToken);
  }

  // Get hubs (BIM 360 hubs, ACC hubs)
  async getHubs(accessToken: string): Promise<{ data: AutodeskHub[] }> {
    return this.makeApiCall('/project/v1/hubs', accessToken);
  }

  // Get projects within a hub
  async getProjects(hubId: string, accessToken: string): Promise<{ data: AutodeskProject[] }> {
    return this.makeApiCall(`/project/v1/hubs/${hubId}/projects`, accessToken);
  }

  // Get project details
  async getProject(hubId: string, projectId: string, accessToken: string): Promise<{ data: AutodeskProject }> {
    return this.makeApiCall(`/project/v1/hubs/${hubId}/projects/${projectId}`, accessToken);
  }

  // Get top-level folders in project
  async getProjectFolders(hubId: string, projectId: string, accessToken: string): Promise<{ data: any[] }> {
    return this.makeApiCall(`/project/v1/hubs/${hubId}/projects/${projectId}/topFolders`, accessToken);
  }

  // Get folder contents
  async getFolderContents(projectId: string, folderId: string, accessToken: string): Promise<{ data: AutodeskFile[] }> {
    return this.makeApiCall(`/data/v1/projects/${projectId}/folders/${folderId}/contents`, accessToken);
  }

  // Get file details
  async getFileDetails(projectId: string, itemId: string, accessToken: string): Promise<{ data: AutodeskFile }> {
    return this.makeApiCall(`/data/v1/projects/${projectId}/items/${itemId}`, accessToken);
  }

  // Download file
  async downloadFile(projectId: string, storageId: string, accessToken: string): Promise<Buffer> {
    const response = await fetch(`${this.baseUrl}/data/v1/projects/${projectId}/storage/${storageId}`, {
      headers: {
        'Authorization': `Bearer ${accessToken}`
      }
    });

    if (!response.ok) {
      throw new Error(`File download failed: ${response.status}`);
    }

    return Buffer.from(await response.arrayBuffer());
  }

  // Upload file to project
  async uploadFile(projectId: string, folderId: string, fileName: string, fileBuffer: Buffer, accessToken: string): Promise<any> {
    // First create storage location
    const storageResponse = await this.makeApiCall(`/data/v1/projects/${projectId}/storage`, accessToken, {
      method: 'POST',
      body: JSON.stringify({
        jsonapi: { version: '1.0' },
        data: {
          type: 'objects',
          attributes: {
            name: fileName
          }
        }
      })
    });

    const uploadUrl = storageResponse.data.attributes.uploadUrl;
    const objectId = storageResponse.data.id;

    // Upload file to storage
    await fetch(uploadUrl, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/octet-stream'
      },
      body: fileBuffer
    });

    // Create file item in folder
    return this.makeApiCall(`/data/v1/projects/${projectId}/folders/${folderId}/items`, accessToken, {
      method: 'POST',
      body: JSON.stringify({
        jsonapi: { version: '1.0' },
        data: {
          type: 'items',
          attributes: {
            displayName: fileName,
            name: fileName
          },
          relationships: {
            tip: {
              data: {
                type: 'versions',
                id: '1'
              }
            },
            parent: {
              data: {
                type: 'folders',
                id: folderId
              }
            },
            storage: {
              data: {
                type: 'objects',
                id: objectId
              }
            }
          }
        },
        included: [{
          type: 'versions',
          id: '1',
          attributes: {
            name: fileName
          }
        }]
      })
    });
  }

  // Search files within a project
  async searchFiles(hubId: string, projectId: string, query: string, accessToken: string): Promise<{ data: AutodeskFile[] }> {
    const params = new URLSearchParams({
      filter: `name:${query}`,
      limit: '100'
    });

    return this.makeApiCall(`/project/v1/hubs/${hubId}/projects/${projectId}/search?${params.toString()}`, accessToken);
  }

  // Get file versions
  async getFileVersions(projectId: string, itemId: string, accessToken: string): Promise<{ data: any[] }> {
    return this.makeApiCall(`/data/v1/projects/${projectId}/items/${itemId}/versions`, accessToken);
  }

  // Test connection with basic API call
  async testConnection(accessToken: string): Promise<boolean> {
    try {
      await this.getUserProfile(accessToken);
      return true;
    } catch (error) {
      console.error('Autodesk connection test failed:', error);
      return false;
    }
  }
}

export default AutodeskConstructionCloudService;

